import React from 'react';

class CoursesBrowse extends React.Component{

  componentDidMount(){
    this.props.loadCourses();
  }

  render(){
    return (
      <div>
          <a class="HomeLink" href="/">back to home</a>
          <div class="h1">
            Browse Courses
          </div>
         <table className="displayTable" style={{width:"100%"}}>
           <tr>
              <th>Course name</th><th>Description</th><th>Owner</th>
           </tr>
           {
             this.props.coursesArray.map(obj=>(
               <tr>
                 <td><a href={`./courseShowSelected/${obj.id}`}>{obj.name}</a></td>
                 <td>{obj.description}</td>
                 <td>{obj.author_id}</td>
               </tr>
               )
             )
           }
         </table>
     </div>

    )
  }

  constructor(props){
    super(props);
    this.state={
      spinner:false,
      objArray:[]
    }
  }
}

export default CoursesBrowse;
