import React from 'react';
import data from './data';
import {  Redirect } from 'react-router-dom';

class QuizInstances extends React.Component{
  constructor(props){
    super(props);
    this.state={
      quizObj:{},
      quizResultsArray:[],
      icons: data.icons
    }
  }

  async componentDidMount(){
    this.props.showSpinner();
    const {params}= this.props.match;

    var reqBody="quizId="+encodeURIComponent(params.id);
    let promise1=fetch(`/api/getTheQuiz`, {
      headers:{
        'Accept':'application/json',
        'Content-type': 'application/x-www-form-urlencoded'
      },
      method: 'POST',
      body: reqBody
    });

    let promise2=fetch(`/api/quizGetScores`, {
                      headers:{
                        'Accept':'application/json',
                        'Content-type': 'application/x-www-form-urlencoded'
                      },
                      method: 'POST',
                      body: reqBody
                    });

    Promise.all([promise1, promise2, promise3]).then(responses=>{
      Promise.all(responses.map(res=>res.json())).then(jsons=>{
          jsons[0].id=params.id;
          //console.log(jsons[0]);
           this.setState({
              quizObj: jsons[0], quizObjForEdit: jsons[0],
              problemsArray: jsons[1], problemsArrayForEdit: jsons[1],
              quizResultsArray:jsons[2]});
           })
    });
    // console.log(data);
     this.props.hideSpinner();

  }

  render(){
    if(this.props.loggedInUser===''){
       return <Redirect to="/errorLogin"/>
    }
    return (
      <div>
          <a class="HomeLink" href="/">
              <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
          {/*back to home*/}
          </a>
          <div class="h1">
            Quiz Results
          </div>
          <div className="Panel">
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
          </div>
          <div  className='Browse'>
               <table className="displayTable" style={{width:"100%"}}>
                 <tr>
                   <th>Marks Scored</th><th>User</th><th>Start Timestamp</th>
                 </tr>
                 {
                   this.state.quizResultsArray.map(obj=>(
                     <tr>
                       <td>{obj.marks_scored}/{obj.maxmarks}</td>
                       <td>{obj.user_id}</td>
                       <td>{obj.start_timestamp}</td>
                     </tr>
                     )
                   )
                 }
               </table>
         </div>
      </div>
    )
  }


}

export default QuizInstances;
