import React from 'react';
import {  Redirect } from 'react-router-dom';
import socketIOClient from "socket.io-client";
import data from "./data";
import '../css/Chat.css';
import VideoExt from './chat/VideoExt';
import SocketX from './chat/SocketX';
import PeerConnectionExt from './chat/PeerConnectionExt';

const ICE_SERVERS=[
  {
    urls: 'turn:numb.viagenie.ca',
    credential: 'muazkh',
    username: 'webrtc@live.com'
  },
  {
      url: 'turn:192.158.29.39:3478?transport=tcp',
      credential: 'JZEOEt2V3Qb0y27GRntt2u2PAYA=',
      username: '28224511:1379330808'
  },
  {
      url: 'turn:turn.anyfirewall.com:443?transport=tcp',
      credential: 'webrtc',
      username: 'webrtc'
  },
  {
      url: 'turn:13.250.13.83:3478?transport=tcp',
      credential: 'YzYNCouZM1mhqhmseWk6',
      username: 'YzYNCouZM1mhqhmseWk6'
  },
  { urls: 'stun:stun.l.google.com:19302' },
  /*{ urls: 'stun:stun1.l.google.com:19302' },
  { urls: 'stun:stun2.l.google.com:19302' },
  { urls: 'stun:stun3.l.google.com:19302' },
  { urls: 'stun:stun4.l.google.com:19302' },*/
  { urls: 'stun:stun.ekiga.net'}
];

class MeetingShowSelected extends React.Component{
  constructor(props){
    super(props);
    this.state={
      icons: data.icons,
      userIds:[],
      userNameMap:{},
      message: 'Select a user on the left menu to start sharing.',
      callMade: false,
    }
    const {params}= this.props.match;

    this.meetingId=params.id;
    this.organiserId=params.organiserId;

    if(this.organiserId!==this.props.loggedInUser){
      this.peerConnections= new RTCPeerConnection({ iceServers: ICE_SERVERS });
    }
    else
      this.peerConnections={};
    //this.socket = socketIOClient(ENDPOINT);
    this.socket = socketIOClient("/"+this.meetingId);

    this.displayMediaOptions = {
      video: {
        cursor: "always"
      },
      audio: {
        echoCancellation: true,
        noiseSuppression: true
      }
    };
    this.camMediaOptions={
      video: true,
      audio: {
        echoCancellation: true,
        noiseSuppression: true
      }
    };
  }

  onSocketConnect=()=>{
    if(this.socket){
      this.socket.emit("map", {
        userName: this.props.loggedInUserFullName,
        userId: this.props.loggedInUser
      });
      console.log(this.socket);
      console.log(' mapped to '+this.props.loggedInUser);
    }
  }

  callUser=async (userId)=>{
    console.log('Call User '+userId+' '+this.state.userNameMap[userId]);
    const offer = await this.peerConnections.createOffer();
    await this.peerConnections.setLocalDescription(new RTCSessionDescription(offer));
    console.log(JSON.stringify(offer));
    //console.log(this.peerConnection);
    this.socket.emit("call-user", {
      offer,
      to: userId,
      from: this.props.loggedInUser
    });
    this.setState({callMade: true, message: `Talking with: user: ${this.state.userNameMap[userId]} (${userId})`});
  }

  onCallRcvd=async (data) => {
    console.log('callRcvd '+this.state.callRcvd);
    if(this.props.loggedInUser===this.this.organiserId){
      this.peerConnections[data.from] = new RTCPeerConnection({ iceServers: ICE_SERVERS });
      await this.peerConnections[data.from].setRemoteDescription(
        new RTCSessionDescription(data.offer)
      );
      const answer = await this.peerConnections[data.from].createAnswer();
      console.log('call received');
      console.log('answer \n'+JSON.stringify(answer));
      await this.peerConnections[data.from].setLocalDescription(new RTCSessionDescription(answer));
      //console.log(this.peerConnection);
      this.socket.emit("make-answer", {
        answer,
        to: data.from,
        from: this.props.loggedInUser
      });
    }
    else{
      await this.peerConnections.setRemoteDescription(
        new RTCSessionDescription(data.offer)
      );
      const answer = await this.peerConnections.createAnswer();
      console.log('call received');
      console.log('answer \n'+JSON.stringify(answer));
      await this.peerConnections.setLocalDescription(new RTCSessionDescription(answer));
      //console.log(this.peerConnection);
      this.socket.emit("make-answer", {
        answer,
        to: data.from,
        from: this.props.loggedInUser
      });

    }
    this.setState({callRcvd: true});

  }

  onUpdateUserList=(data) => {
    let userIds=[...this.state.userIds];
    let newUserIds=data.userIds;
    newUserIds.forEach(id=>{
      if(!userIds.includes(id))
        userIds.push(id);
    });

    console.log('In update user list');
    console.log(userIds);
    console.log(data.nameMap);
    this.setState({userIds, userNameMap: data.nameMap}, ()=>{
      if(userIds.includes(this.organiserId) && !this.state.callMade)
        this.callUser(this.organiserId);
    });
  };

  onRemoveUser=({ userId }) => {
    console.log('removing user '+userId);
    let userIds=this.state.userIds.filter((val)=>val!==userId);
    let userNameMap={};
    for(let attr in this.state.userNameMap){
      if(userIds.includes(attr))
        userNameMap[attr]=this.state.userNameMap[attr];
    }
    if(userId===this.organiserId)
      this.setState({userNameMap, userIds, callMade: false});
    else
     this.setState({userNameMap, userIds});
  }

  componentDidMount(){

  }

  render(){
      if(this.props.loggedInUserFullName===''){
        return <Redirect to="/errorLogin"/>
      }
      else
      {
          return (
            <div>
            Meeting Show Selected
             <SocketX
              socket={this.socket}
              onSocketConnect={this.onSocketConnect}
              onUpdateUserList={this.onUpdateUserList}
              onRemoveUser={this.onRemoveUser}
              />
              <div className="content-container">
                    <div className="active-users-panel"  id="active-user-container">
                      <h3 className="panel-title">Callable Users:</h3>
                      {this.state.userIds.map(val=>(<div id={val} key={val}    className="active-user">
                                                        <p className="username">
                                                          {this.state.userNameMap[val]}({val})
                                                        </p>
                                                       </div>
                                                       )
                                            )}
                    </div>
              </div>
            </div>
          )
      }
  }
}

export default MeetingShowSelected;
