import React from 'react';
import data from './data';
import '../css/Browse.css';

class UsersBrowse extends React.Component{
  componentDidMount(){
    /*this.setState({spinner:true});
    fetch(`/api/getUsers`, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      this.setState({spinner:false, objArray: data});
    })*/
    this.props.loadUsers();
  }

  constructor(props){
    super(props);
    this.state={
      avatar: data.avatar
    }
  }


  render(){
    return (
      <div>
          <a className="HomeLink" href="/">back to home</a>
          <div className="h1">
            Browse Users
          </div>
          <div  className='Browse'>
          <table className="displayTable">
           <tr>
             <th style={{width:'20em'}}>image</th><th>user name</th><th>full name</th><th>city</th><th>zip</th><th>email</th>
           </tr>
             {
               this.props.usersArray.map(obj=>(
                 <tr>
                   <td style={{width:'20em'}}>
                   <div className="profileCard">
                      <img src={obj.sex_male?this.state.avatar.male:this.state.avatar.female} style={{width:'100%'}} alt="Avatar"/>
                   </div>
                   </td>
                   <td>{obj.id}</td>
                   <td><a href={`./userShowSelected/${obj.id}`}>{obj.first_name+' '+obj.last_name}</a></td>
                   <td>{obj.city}</td>
                   <td>{obj.zip}</td>
                   <td>{obj.email}</td>
                 </tr>
                 )
               )
             }

         </table>
         </div>
     </div>
    )
  }


}

export default UsersBrowse;
