import React from 'react';
import './css/App.css';
import Header from './components/Header';
import ImageSlider from './components/ImageSlider';
import DropdownMenuHover from './components/DropdownMenuHover';
import ProblemsBrowse from './components/ProblemsBrowse';
import ProblemInsert from './components/ProblemInsert';
import ProblemShowSelected from './components/ProblemShowSelected';
import QuizesBrowse from './components/QuizesBrowse';
import QuizInsert from './components/QuizInsert';
import QuizShowSelected from './components/QuizShowSelected';
import UsersBrowse from './components/UsersBrowse';
import UserInsert from './components/UserInsert';
import UserShowSelected from './components/UserShowSelected';
import CoursesBrowse from './components/CoursesBrowse';
import CourseInsert from './components/CourseInsert';
import CourseShowSelected from './components/CourseShowSelected';
import { Route, BrowserRouter as Router} from 'react-router-dom';
import {getMinutesRemaining, getCurrentTimestampString, getPageArray} from './utils/utils';

class App extends React.Component {

  constructor(props){
    super(props);
    this.state={
      spinner:false,
      userId:null,
      password:null,
      loggedInUser:localStorage.getItem('loggedInUser') || '',
      admin:localStorage.getItem('admin')||"0",
      errorMessage:null,
      coursesArray:[],
      problemsArray:[],
      currentProblemsPage:1,
      currentQuizesPage:1,
      currentCoursesPage:1,
      currentUsersPage:1,
      pageSize:100,
      quizesArray:[],
      usersArray:[],
      startedQuiz:localStorage.getItem('startedQuiz')?JSON.parse(localStorage.getItem('startedQuiz')):null,
      startedQuizAnswers:localStorage.getItem('startedQuizAnswers')?JSON.parse(localStorage.getItem('startedQuizAnswers')):null
    }
    this.userIdChange=this.userIdChange.bind(this);
    this.passwordChange=this.passwordChange.bind(this);
    this.handleLoginSubmit=this.handleLoginSubmit.bind(this);
    this.logoutUser=this.logoutUser.bind(this);
    this.setErrorMessage=this.setErrorMessage.bind(this);
    this.setCourses=this.setCourses.bind(this);
    this.setProblems=this.setProblems.bind(this);
    this.setQuizes=this.setQuizes.bind(this);
    this.setUsers=this.setUsers.bind(this);
    this.loadCourses=this.loadCourses.bind(this);
    this.loadProblems=this.loadProblems.bind(this);
    this.loadQuizes=this.loadQuizes.bind(this);
    this.loadUsers=this.loadUsers.bind(this);
    this.showSpinner=this.showSpinner.bind(this);
    this.hideSpinner=this.hideSpinner.bind(this);
    this.startQuiz=this.startQuiz.bind(this);
    this.isQuizStarted=this.isQuizStarted.bind(this);
    this.startedQuizOptionChange=this.startedQuizOptionChange.bind(this);
    this.startedQuizSubmit=this.startedQuizSubmit.bind(this);
    this.nextProblemsPageClick=this.nextProblemsPageClick.bind(this);
    this.prevProblemsPageClick=this.prevProblemsPageClick.bind(this);
    this.setProblemsPage=this.setProblemsPage.bind(this);
    this.nextQuizesPageClick=this.nextQuizesPageClick.bind(this);
    this.prevQuizesPageClick=this.prevQuizesPageClick.bind(this);
    this.setQuizesPage=this.setQuizesPage.bind(this);
    this.nextCoursesPageClick=this.nextCoursesPageClick.bind(this);
    this.prevCoursesPageClick=this.prevCoursesPageClick.bind(this);
    this.setCoursesPage=this.setCoursesPage.bind(this);
    this.nextUsersPageClick=this.nextUsersPageClick.bind(this);
    this.prevUsersPageClick=this.prevUsersPageClick.bind(this);
    this.setUsersPage=this.setUsersPage.bind(this);
  }

  componentDidMount() {
    var timer = setInterval(() => {this.reviseQuizMinsRemaining();}, 60000)
  }






  loadCourses(){
    this.setState({spinner:true});
    fetch(`/api/getCourses`, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      //console.log(data);
      this.setState({coursesArray: data, spinner: false});
    })
  }

  setCourses(arr){
    this.setState({coursesArray:arr});
  }

  setCoursesPage(page){
    this.setState({currentCoursesPage:page}, ()=>{this.loadCourses();});
  }

  nextCoursesPageClick(e){
    let pageArray=getPageArray(this.state.currentCoursesPage, this.state.coursesArray===undefined||this.state.coursesArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentCoursesPage+1)){
        this.setState({currentCoursesPage:this.state.currentCoursesPage+1}, ()=>{this.loadCourses()});
    }
  }

  prevCoursesPageClick(e){
    let pageArray=getPageArray(this.state.currentCoursesPage, this.state.coursesArray===undefined||this.state.coursesArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentCoursesPage-1)){
        this.setState({currentCoursesPage:this.state.currentCoursesPage-1}, ()=>{this.loadCourses()});
    }
  }


  startedQuizOptionChange(problemId, optionSelectedValue){
    const copied = Object.assign({}, this.state.startedQuizAnswers);
    copied[problemId]=optionSelectedValue;
    this.setState({startedQuizAnswers:copied});
    //console.log(copied);
    localStorage.setItem('startedQuizAnswers', JSON.stringify(copied));
  }

  startQuiz(quizObj){
    if(this.state.startedQuiz===null){
      if(window.confirm('Are you sure you want to start the quiz:\r\n'+quizObj.description)===true){
        quizObj['start_time']=getCurrentTimestampString();
        var reqBody="quizId="+encodeURIComponent(quizObj.id);
            reqBody+='&startTime='+encodeURIComponent(quizObj.start_time);
            reqBody+='&userId='+encodeURIComponent(this.state.loggedInUser);

        fetch(`/api/quizStart`, {
          headers:{
            'Accept':'application/json',
            'Content-type': 'application/x-www-form-urlencoded'
          },
          method: 'POST',
          body: reqBody
        })
        .then(res=>res.json())
        .then(
          data=>{
            console.log(data);
            if(data.insertstatus=== "ok"){
              quizObj['quizInstanceId']=data.quizInstanceId;
              this.setState({startedQuiz:quizObj});
              localStorage.setItem('startedQuiz', JSON.stringify(quizObj));
            }
            else{
              alert('An error occurred in connecting to the server to start the quiz');
            }
          });
      }
    }
    else{
      alert('There is already a started quiz:\r\n'+
             this.state.startedQuiz.description+
            '\r\n Please submit this quiz to start a new quiz')
    }
     console.log(this.state.startedQuiz);
  }

  startedQuizSubmit(){
    console.log(this.state.startedQuizAnswers);
    console.log(this.state.startedQuiz);

    var reqBody="quizId="+encodeURIComponent(this.state.startedQuiz.id);
        reqBody+='&quizInstanceId='+encodeURIComponent(this.state.startedQuiz.quizInstanceId);
        reqBody+='&answersObject='+encodeURIComponent(JSON.stringify(this.state.startedQuizAnswers));

        fetch(`/api/quizAnwersSubmit`, {
          headers:{
            'Accept':'application/json',
            'Content-type': 'application/x-www-form-urlencoded'
          },
          method: 'POST',
          body: reqBody
        })
        .then(res=>res.json())
        .then(
          data=>{
            //console.log(data);
            if(data.insertstatus=== "ok"){
              localStorage.removeItem('startedQuiz');
              localStorage.removeItem('startedQuizAnswers');
              this.setState({startedQuiz:null, startedQuizAnswers:null});
              alert('Quiz Submitted');
            }
          }
        );
  }

  reviseQuizMinsRemaining(){
    if(this.state.startedQuiz!==null){
          var mins_remaining= getMinutesRemaining(this.state.startedQuiz.start_time, this.state.startedQuiz.duration_minutes);

          if(mins_remaining>0){
            console.log(mins_remaining);
            const copied = Object.assign({}, this.state.startedQuiz);
            //copied['mins_remaining']=mins_remaining;
            this.setState({startedQuiz:copied});
            localStorage.setItem('startedQuiz', JSON.stringify(copied));
         }
         else{
           this.startedQuizSubmit();
         }
    }
  }



  isQuizStarted(quizId){
    if(this.state.startedQuiz!==null&&this.state.startedQuiz.id===quizId)
        return true;
    else
        return false;
  }


  loadProblems(){
    let url='/api/getProblems';

    if(this.state.currentProblemsPage!==undefined&&this.state.pageSize!==undefined){
      url+='?currentPage='+this.state.currentProblemsPage+'&pageSize='+this.state.pageSize;
    }

    this.setState({spinner:true});
    fetch(url, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
        this.setState({problemsArray: data, spinner: false});
    })
  }

  setProblems(arr){
    this.setState({problemsArray:arr});
  }

  setProblemsPage(page){
    this.setState({currentProblemsPage:page}, ()=>{this.loadProblems();});
  }

  nextProblemsPageClick(e){
    let pageArray=getPageArray(this.state.currentProblemsPage, this.state.problemsArray===undefined||this.state.problemsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentProblemsPage+1)){
        this.setState({currentProblemsPage:this.state.currentProblemsPage+1}, ()=>{this.loadProblems()});
    }
  }

  prevProblemsPageClick(e){
    let pageArray=getPageArray(this.state.currentProblemsPage, this.state.problemsArray===undefined||this.state.problemsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentProblemsPage-1)){
        this.setState({currentProblemsPage:this.state.currentProblemsPage-1}, ()=>{this.loadProblems()});
    }
  }


  loadQuizes(){
    this.setState({spinner:true});
    fetch(`/api/getQuizes`, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      //console.log(data);
      this.setState({ quizesArray: data, spinner: false});
    })
  }

  setQuizes(arr){
    this.setState({quizesArray:arr});
  }

  setQuizesPage(page){
    this.setState({currentProblemsPage:page}, ()=>{this.loadProblems();});
  }

  nextQuizesPageClick(e){
    let pageArray=getPageArray(this.state.currentQuizesPage, this.state.quizesArray===undefined||this.state.quizesArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentQuizesPage+1)){
        this.setState({currentQuizesPage:this.state.currentQuizesPage+1}, ()=>{this.loadQuizes()});
    }
  }

  prevQuizesPageClick(e){
    let pageArray=getPageArray(this.state.currentQuizesPage, this.state.quizesArray===undefined||this.state.quizesArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentQuizesPage-1)){
        this.setState({currentQuizesPage:this.state.currentQuizesPage-1}, ()=>{this.loadQuizes()});
    }
  }

  loadUsers(){
    this.setState({spinner:true});
    fetch(`/api/getUsers`, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      this.setState({usersArray: data, spinner:false});
    })
  }

  setUsers(arr){
    this.setState({usersArray:arr});
  }

  setUsersPage(page){
    this.setState({currentUsersPage:page}, ()=>{this.loadUsers();});
  }

  nextUsersPageClick(e){
    let pageArray=getPageArray(this.state.currentUsersPage, this.state.usersArray===undefined||this.state.usersArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentUsersPage+1)){
        this.setState({currentUsersPage:this.state.currentUsersPage+1}, ()=>{this.loadUsers()});
    }
  }

  prevUsersPageClick(e){
    let pageArray=getPageArray(this.state.currentUsersPage, this.state.usersArray===undefined||this.state.usersArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentUsersPage-1)){
        this.setState({currentUsersPage:this.state.currentUsersPage-1}, ()=>{this.loadUsers()});
    }
  }

  showSpinner(){
    this.setState({spinner:true});
  }

  hideSpinner(){
    this.setState({spinner:false});
  }

  handleLoginSubmit(event){
    if(this.state.userId===null
        ||this.state.password===null
        ||this.state.password.length===0
        ||this.state.userId.length===0)
    {
      event.preventDefault();
      this.setState({errorMessage: "UserId/password cannot be empty"})
      return;
    }
    var reqBody="userId="+encodeURIComponent(this.state.userId);
        reqBody+='&password='+encodeURIComponent(this.state.password);

        fetch(`/api/login`, {
          headers:{
            'Accept':'application/json',
            'Content-type': 'application/x-www-form-urlencoded'
          },
          method: 'POST',
          body: reqBody
        })
        .then(res=>res.json())
        .then(
          data=>{
            console.log(data);
            if(data.login==="ok"){
              localStorage.setItem('loggedInUser', this.state.userId);
              localStorage.setItem('admin', data.admin);
              this.setState({errorMessage:null, loggedInUser: this.state.userId, admin: data.admin});
            }
            else{
              this.setState({errorMessage: "Login Failed: UserId/password Mismatch"});
            }
          }
        );
    event.preventDefault();

  }

  userIdChange(e){
    const newUserId=e.target.value;
    this.setState({userId: newUserId});
  }

  passwordChange(e){
    const newPassword=e.target.value;
    this.setState({password: newPassword});
  }

  logoutUser(e){
    //e.preventDefault();
    let confirmMsg='Are you sure you want to logout?';
    if(this.state.startedQuiz!==null){
        confirmMsg+='There is an unsubmitted quiz: \r\n'+this.state.startedQuiz.description;
    }
    let b=window.confirm(confirmMsg)
    if(b===true){
      if(this.state.startedQuiz!==null)
         this.startedQuizSubmit();
      localStorage.setItem('loggedInUser', '');
      localStorage.setItem('admin', "0");
      localStorage.removeItem('startedQuiz');
      localStorage.removeItem('startedQuizAnswers');
      this.setState({errorMessage:null, loggedInUser: '', admin:"0",
                      startedQuiz:null, startedQuizAnswers:null});
    }
    else{
      e.preventDefault();
    }
  }

  setErrorMessage(errMsg){
    this.setState({errorMessage:errMsg});
  }


    render(){
      return (
        <Router>
          <div>
            <Route path="/" render={(props)=><Header
                                                {...props}
                                                userId={this.state.userId}
                                                password={this.state.password}
                                                errorMessage={this.state.errorMessage}
                                                passwordChange={this.passwordChange}
                                                userIdChange={this.userIdChange}
                                                handleSubmit={this.handleLoginSubmit}
                                                logoutUser={this.logoutUser}
                                                loggedInUser={this.state.loggedInUser}
                                                startedQuiz={this.state.startedQuiz}
                                              />}
            />
            <Route path="/" render={(props)=><DropdownMenuHover
                                              {...props}
                                               loggedInUser={this.state.loggedInUser}
                                               setErrorMessage={this.setErrorMessage}
                                              />
                                    }
             />
             {
               this.state.spinner===true?(<div id="spinner" className="loadingContainer"><div className="loading"></div></div>):null
             }
            <Route exact path="/" component={ImageSlider} />
            <Route path="/problemsBrowse" render={(props)=><ProblemsBrowse {...props}
                                                            problemsArray={this.state.problemsArray}
                                                            quizesArray={this.state.quizesArray}
                                                            loadProblems={this.loadProblems}
                                                            setProblems={this.setProblems}
                                                            loadQuizes={this.loadQuizes}
                                                            showSpinner={this.showSpinner}
                                                            hideSpinner={this.hideSpinner}
                                                            currentPage={this.state.currentProblemsPage}
                                                            pageArray={getPageArray(this.state.currentProblemsPage, this.state.problemsArray===undefined||this.state.problemsArray.length<this.state.pageSize)}
                                                            nextPageClick={this.nextProblemsPageClick}
                                                            prevPageClick={this.prevProblemsPageClick}
                                                            setProblemsPage={this.setProblemsPage}
                                                            loggedInUser={this.state.loggedInUser}
                                                            admin={this.state.admin}
                                                            />
                                                 }
            />
            <Route path="/problemInsert" render={(props)=><ProblemInsert {...props}
                                                           problemsArray={this.state.problemsArray}
                                                           quizesArray={this.state.quizesArray}
                                                           loadQuizes={this.loadQuizes}
                                                           setProblems={this.setProblems}
                                                           showSpinner={this.showSpinner}
                                                           hideSpinner={this.hideSpinner}
                                                           loggedInUser={this.state.loggedInUser}
                                                          />
                                                }
            />
            <Route path="/problemShowSelected/:id" render={(props)=><ProblemShowSelected {...props}
                                                                  loadProblems={this.loadProblems}
                                                                  problemsArray={this.state.problemsArray}
                                                                  setProblems={this.setProblems}
                                                                  loadQuizes={this.loadQuizes}
                                                                  quizesArray={this.state.quizesArray}
                                                                  showSpinner={this.showSpinner}
                                                                  hideSpinner={this.hideSpinner}
                                                                  loggedInUser={this.state.loggedInUser}
                                                                  admin={this.state.admin}
                                                                  setErrorMessage={this.setErrorMessage}
                                                                 />
                                                       }
            />
            <Route path="/quizesBrowse" render={(props)=><QuizesBrowse
                                                          quizesArray={this.state.quizesArray}
                                                          loadQuizes={this.loadQuizes}
                                                          pageArray={getPageArray(this.state.currentQuizesPage, this.state.quizesArray===undefined||this.state.quizesArray.length<this.state.pageSize)}
                                                          nextPageClick={this.nextQuizesPageClick}
                                                          prevPageClick={this.prevQuizesPageClick}
                                                          setProblemsPage={this.setQuizesPage}
                                                        />
                                               }
             />
            <Route path="/quizInsert" render={(props)=><QuizInsert {...props}
                                                        quizesArray={this.state.quizesArray}
                                                        coursesArray={this.state.coursesArray}
                                                        loadCourses={this.loadCourses}
                                                        setQuizes={this.setQuizes}
                                                        showSpinner={this.showSpinner}
                                                        hideSpinner={this.hideSpinner}
                                                        loggedInUser={this.state.loggedInUser}
                                                       />
                                             }
            />
            <Route path="/quizShowSelected/:id" render={(props)=><QuizShowSelected {...props}
                                                                  loadQuizes={this.loadQuizes}
                                                                  quizesArray={this.state.quizesArray}
                                                                  setQuizes={this.setQuizes}
                                                                  loadCourses={this.loadCourses}
                                                                  coursesArray={this.state.coursesArray}
                                                                  showSpinner={this.showSpinner}
                                                                  hideSpinner={this.hideSpinner}
                                                                  loggedInUser={this.state.loggedInUser}
                                                                  startQuiz={this.startQuiz}
                                                                  admin={this.state.admin}
                                                                  setErrorMessage={this.setErrorMessage}
                                                                  isQuizStarted={this.isQuizStarted}
                                                                  optionChange={this.startedQuizOptionChange}
                                                                  submitQuiz={this.startedQuizSubmit}
                                                                  startedQuizAnswers={this.state.startedQuizAnswers}
                                                                 />
                                                       }
            />
            <Route path="/usersBrowse" render={(props)=><UsersBrowse {...props}
                                                          usersArray={this.state.usersArray}
                                                          loadUsers={this.loadUsers}
                                                          pageArray={getPageArray(this.state.currentUsersPage, this.state.usersArray===undefined||this.state.usersArray.length<this.state.pageSize)}
                                                          nextPageClick={this.nextUsersPageClick}
                                                          prevPageClick={this.prevUsersPageClick}
                                                          setProblemsPage={this.setUsersPage}
                                                        />
                                              }
            />
            <Route path="/userInsert" render={(props)=><UserInsert {...props}
                                                        usersArray={this.state.usersArray}
                                                        setUsers={this.setUsers}
                                                        showSpinner={this.showSpinner}
                                                        hideSpinner={this.hideSpinner}
                                                       />
                                             }
            />
            <Route path="/userShowSelected/:id" render={(props)=><UserShowSelected {...props}
                                                                  loadUsers={this.loadUsers}
                                                                  usersArray={this.state.usersArray}
                                                                  setUsers={this.setUsers}
                                                                  showSpinner={this.showSpinner}
                                                                  hideSpinner={this.hideSpinner}
                                                                  loggedInUser={this.state.loggedInUser}
                                                                  admin={this.state.admin}
                                                                  setErrorMessage={this.setErrorMessage}
                                                                 />
                                                       }
            />
            <Route path="/coursesBrowse" render={(props)=><CoursesBrowse
                                                           coursesArray={this.state.coursesArray}
                                                           loadCourses={this.loadCourses}
                                                           pageArray={getPageArray(this.state.currentCoursesPage, this.state.coursesArray===undefined||this.state.coursesArray.length<this.state.pageSize)}
                                                           nextPageClick={this.nextCoursesPageClick}
                                                           prevPageClick={this.prevCoursesPageClick}
                                                           setProblemsPage={this.setCoursesPage}
                                                          />
                                                }
            />
            <Route path="/courseInsert" render={(props)=><CourseInsert {...props}
                                                          coursesArray={this.state.coursesArray}
                                                          setCourses={this.setCourses}
                                                          showSpinner={this.showSpinner}
                                                          hideSpinner={this.hideSpinner}
                                                          loggedInUser={this.state.loggedInUser}
                                                          />
                                               }
            />
            <Route path="/CourseShowSelected/:id" render={(props)=><CourseShowSelected {...props}
                                                                    loadCourses={this.loadCourses}
                                                                    coursesArray={this.state.coursesArray}
                                                                    setCourses={this.setCourses}
                                                                    showSpinner={this.showSpinner}
                                                                    hideSpinner={this.hideSpinner}
                                                                    loggedInUser={this.state.loggedInUser}
                                                                    admin={this.state.admin}
                                                                    setErrorMessage={this.setErrorMessage}
                                                                  />
                                                          }
            />
            {
            this.state.loggedInUser==="" && <Route path="/errorLogin" render={(props)=>(<div
                                                           style={{color:"red", fontSize: "1.5em",
                                                             paddingTop:"1%",
                                                            textAlign:"center"}}>
                                                            Error: User not Logged In!
                                                       </div>
                                                       )
                                              }
           />
           }

          </div>
        </Router>
      )
    }


}

export default App;
