import React from 'react';
import data from './data';
import {  Redirect } from 'react-router-dom';
import QuizInstanceProblemsDialog from './QuizInstanceProblemsDialog';

class InputDialog extends React.Component{

  constructor(props){
    super(props);
  }

  render(){
    const visibilityClassName=this.props.show
    ? "simple-dialog-overlay simple-dialog-display-block"
    :"simple-dialog-overlay simple-dialog-display-none";

    return (
          <div className={visibilityClassName}>
             <div className="simple-input-dialog">
                    <input type="text" value={this.props.editObject.marks_awarded}
                    onChange={this.props.handleEdit}/>
                    <button id="x" style={{width:'100px'}}
                     onClick={this.props.handleDismiss}
                    >close
                    </button>
                   <input type="button" id="x" value="Save" style={{width:'100px', float: "right"}}
                   onClick={this.props.handleSave}
                   value="save"/>
              </div>
          </div>
        );
  }

}

class QuizInstances extends React.Component{
  constructor(props){
    super(props);
    this.state={
      quizObj:{},
      quizInstancesArray:[],
      quizInstanceObjectSelected:{},
      quizInstanceProblemsArray:[],
      quizInstanceProblemsArrayForEdit:[],
      quizInstanceProblemObjectSelected:{},
      quizInstanceProblemObjectSelectedForEdit:{},
      showInstanceProblemsDialog:false,
      showMarksEditDialog:false,
      icons: data.icons
    }
    this.handleQuizInstanceProblemsDialogDismiss=this.handleQuizInstanceProblemsDialogDismiss.bind(this);
    this.showQuizInstanceProblemsHandler=this.showQuizInstanceProblemsHandler.bind(this);
    this.showMarksEditDialog=this.showMarksEditDialog.bind(this);
    this.hideMarksEditDialog=this.hideMarksEditDialog.bind(this);
    this.marksAwardedChanged=this.marksAwardedChanged.bind(this);
    this.handleMarksEditSave=this.handleMarksEditSave.bind(this);
  }

  componentDidMount(){
    this.props.showSpinner();
    const {params}= this.props.match;

    var reqBody="quizId="+encodeURIComponent(params.id);

    let promise1=fetch(`/api/getTheQuiz`, {
      headers:{
        'Accept':'application/json',
        'Content-type': 'application/x-www-form-urlencoded'
      },
      method: 'POST',
      body: reqBody
    });

    let promise2=fetch(`/api/quizGetScores`, {
                      headers:{
                        'Accept':'application/json',
                        'Content-type': 'application/x-www-form-urlencoded'
                      },
                      method: 'POST',
                      body: reqBody
                    });

    Promise.all([promise1, promise2]).then(responses=>{
      Promise.all(responses.map(res=>res.json())).then(jsons=>{
          jsons[0].id=params.id;
          //console.log(jsons[0]);
           this.setState({
              quizObj: jsons[0],
              quizInstancesArray:jsons[1]
           });
      })
    });
     this.props.hideSpinner();

  }

  handleQuizInstanceProblemsDialogDismiss(e){
     this.setState({showQuizInstanceProblemsDialog:false});
  }


  showQuizInstanceProblemsHandler(e, obj){
    //alert(obj.quiz_instance_id);
    var reqBody="quizInstanceId="+encodeURIComponent(obj.quiz_instance_id);

    fetch(`/api/getQuizInstanceProblems`, {
      headers:{
        'Accept':'application/json',
        'Content-type': 'application/x-www-form-urlencoded'
      },
      method: 'POST',
      body: reqBody
    })
    .then(res=>res.json())
    .then(data=>{
        this.setState({quizInstanceProblemsArray:data, quizInstanceProblemsArrayForEdit: data, quizInstanceObjectSelected:obj, showQuizInstanceProblemsDialog:true});
     });

  }

  hideMarksEditDialog(e){
    this.setState({showMarksEditDialog: false});
  }

  handleMarksEditSave(e){
    let obj=this.state.quizInstanceProblemObjectSelectedForEdit;
    console.log(obj);
    let quizInstanceProblemsArray=[];
    this.state.quizInstanceProblemsArrayForEdit.forEach(val=>{
      if(val.problem_id!==obj.problem_id)
          quizInstanceProblemsArray.push(val);
      else{
        let newObj=Object.assign({}, val);
        newObj.marks_awarded=obj.marks_awarded;
        quizInstanceProblemsArray.push(newObj);
      }
    })
    /*let new_marks_awarded=e.target.value;
    let newObj=Object.assign({}, arr[index]);
      newObj.marks_awarded=new_marks_awarded;
    let quizInstanceProblemsArray=[];
    this.state.quizInstanceProblemsArrayForEdit.forEach(val=>{
      if(val.problem_id!==obj.problem_id)
          quizInstanceProblemsArray.push(val);
      else{
        let newObj=Object.assign({}, val);
        newObj.marks_awarded=new_marks_awarded;
        quizInstanceProblemsArray.push(newObj);
      }
    })
    this.setState({quizInstanceProblemsArrayForEdit:arr});
    console.log(new_marks_awarded);*/

  }

  showMarksEditDialog(e, obj){
    console.log(obj);
    this.setState({showMarksEditDialog: true, quizInstanceProblemObjectSelected: obj, quizInstanceProblemObjectSelectedForEdit: obj});
  }

  marksAwardedChanged(e){
     let newObj=Object.assign({},this.state.quizInstanceProblemObjectSelectedForEdit)
     newObj.marks_awarded=e.target.value;
     this.setState({quizInstanceProblemObjectSelectedForEdit:newObj})
     //console.log(e.target.value);
  }

  render(){
    if(this.props.loggedInUser===''){
       return <Redirect to="/errorLogin"/>
    }
    return (
      <div>
          <a class="HomeLink" href="/">
              <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
          {/*back to home*/}
          </a>
          <div class="h1">
            Quiz Instances Details
          </div>
          <div className="Panel">
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
                <div className="h1">
                  {this.state.quizObj.description}
                </div>
                 <b>Course:</b><br/>
                 {this.state.quizObj.coursesArray!==undefined?
                   (
                     this.state.quizObj.coursesArray.map(courseObj=>(
                                     <a href={`http://${window.location.hostname}:${window.location.port}/courseShowSelected/${courseObj.id}`}>
                                       <div id={`courseName$,${courseObj.id}`}  className="Course" style={{marginLeft:'2em'}}>
                                       {courseObj.name}
                                       </div>
                                     </a>
                                                                     )
                     )
                   ):null
                 }
                 <br/>
                 <b>Duration (minutes):</b> {this.state.quizObj.duration_minutes}
                 <br/><br/>
                 <b> Quiz Type:</b> {this.state.quizObj.type==="d"?"Descriptive": "Multiple Choice"}
                 <br/><br/>
                <b> Instructor:</b> {this.state.quizObj.instructorId}
          </div>
          <div class="h2">
            Quiz Results
          </div>
          <div  className='Browse'>
               <table className="displayTable" style={{width:"100%"}}>
                 <tr>
                   <th>Marks Scored</th><th>User</th><th>Start Time</th><th>End Time</th><th>Evaluate</th>
                 </tr>
                 {
                   this.state.quizInstancesArray.map(obj=>(
                     <tr>
                       <td>{obj.marks_scored}/{obj.maxmarks}</td>
                       <td>{obj.user_id}</td>
                       <td>{obj.start_timestamp}</td>
                       <td>{obj.end_timestamp}</td>
                       <td><input type="button" value="show problems" onClick={(e)=>this.showQuizInstanceProblemsHandler(e, obj)}/></td>
                     </tr>
                     )
                   )
                 }
               </table>
         </div>
         <QuizInstanceProblemsDialog
                 quizInstance={this.state.quizInstanceObjectSelected}
                 quizInstanceProblemsArray={this.state.quizInstanceProblemsArrayForEdit}
                 showMarksEditDialog={this.showMarksEditDialog}
                 show={this.state.showQuizInstanceProblemsDialog}
                 handleDismiss={this.handleQuizInstanceProblemsDialogDismiss}
          />
          <InputDialog
            editObject={this.state.quizInstanceProblemObjectSelectedForEdit}
            show={this.state.showMarksEditDialog}
            handleDismiss={this.hideMarksEditDialog}
            handleSave={this.handleMarksEditSave}
            handleEdit={this.marksAwardedChanged}
          />
      </div>
    )
  }


}

export default QuizInstances;
