import React from 'react';
import '../css/AddToDialog.css';

class ProblemEditDialog extends React.Component{

  constructor(props){
    super(props);
    this.state={
      searchKey: '',
      objectsFiltered:[]
    }

    this.searchBoxChange=this.searchBoxChange.bind(this);
    this.objectsFilteredClick=this.objectsFilteredClick.bind(this);
  }

  objectsFilteredClick(str, e){
    this.setState({searchKey: str, objectsFiltered: []})
  }



  searchBoxChange(e){
    const objectsFiltered=e.target.value!==''?this.props.quizesArray.filter((val)=>val.description.toUpperCase().startsWith(e.target.value.toUpperCase())):[];
    this.setState({searchKey: e.target.value, objectsFiltered: objectsFiltered});
  }

  render(){
    const visibilityClassName=this.props.show
    ? "simple-dialog-overlay simple-dialog-display-block"
    : "simple-dialog-overlay simple-dialog-display-none";

    const obj=this.props.objectForEdit;

    if(obj!==undefined){
      console.log(obj);
    }

    const quizesArray=this.props.quizesArray;

    return (
      <div className={visibilityClassName}>
         <div className="simple-dialog" id="ProblemEditDialog">
             <button id = "x" onClick={this.props.handleDismiss}>
              X
             </button>
             <h2>Edit Problem</h2>
             <hr class="rounded"/>
                 <div className="row">
                     <div id="flexColLeft">
                     Quizes:
                     <div>
                     {
                         obj.quizesArray!==undefined?
                         ( obj.quizesArray.map(objQ=>(
                             <div id={objQ.id} className="chip">{objQ.description}
                              <span className="closeBtn"
                                onClick={(e)=>this.props.removeQuizFromQuizesArray(e, objQ.id)}>&times;</span>
                             </div>
                             )
                           )
                         ):null
                     }
                     </div>
                     <div className="autocomplete">
                        <input id="searchBox" type="text" name="searchBox"
                        value={this.state.searchKey}
                        onChange={this.searchBoxChange} placeholder="search key"/>
                        <div className="autocomplete-items">
                        {
                          this.state.objectsFiltered.map(obj=>(<div onClick={(e)=>this.objectsFilteredClick(obj.description, e)}><strong>{obj.description.substring(0,this.state.searchKey.length)}</strong>{obj.description.substring(this.state.searchKey.length)}</div>))
                        }
                        </div>
                     </div>
                     <input type="submit" value="Add Quiz" onClick={this.props.addQuizToQuizesArraySubmit}/>
                       {/*<select id="quizId" onChange={this.props.selectChange} name="quizId">
                           <option value="null">UnAssigned</option>
                       {
                           quizesArray.map(objQ=>(
                             <option value={objQ.id}>{objQ.description}</option>
                             )
                           )
                       }
                       </select>*/} <br/><br/>
                       Problem Description:<br/>
                         <textarea id="probDescription" name="probDescription" type="text" rows="10"
                         value={obj.description}
                         onChange={this.props.descriptionChange}
                         style={{width:'90%'}}
                         required="true">
                         </textarea><br/><br/>
                         Answer Description:<br/>
                         <textarea id="ansDescription" name="ansDescription" type="text" rows="10"
                         value={obj.solution}
                         onChange={this.props.solutionChange}
                         style={{width:'90%'}}
                         required="true">
                         </textarea>
                     </div>
                     <div id="flexColRight">
                     <br/><br/><br/>
                          Option1: <br/>
                          <input type="text" style={{width:'50%'}}
                                     onChange={this.props.option1Change}
                                     id="option1" value={obj.option1}/><br/>
                          Option2: <br/>
                          <input type="text" style={{width:'50%'}}
                                   onChange={this.props.option2Change}
                                   id="option2" value={obj.option2}/><br/>
                          Option3: <br/>
                          <input type="text" style={{width:'50%'}}
                                   onChange={this.props.option3Change}
                                   id="option3" value={obj.option3}/><br/>
                          Option4: <br/>
                          <input type="text" style={{width:'50%'}}
                                     onChange={this.props.option4Change}
                                     id="option4" value={obj.option4}/><br/>
                          Answer Key: <br/>
                          <input type="text" id="answerKey"
                          onChange={this.props.answerkeyChange}
                          style={{width:'50%'}} value={obj.answerkey}/>
                          <br/>
                          <br/>
                          Author:<br/>
                          <input id="authorName" name="authorName" type="text" style={{width:'80%'}}
                          required="true" value={obj.author_id} readonly>
                          </input> <br/><br/><br/><br/>
                         <input type="button" id="x" value="Save" style={{width:'80%', float: "right"}}
                         onClick={this.props.handleSubmit}/><br/><br/>
                         <button id="x" style={{width:'80%', textAlign:'center'}}
                          onClick={this.props.handleDismiss}>close
                         </button>
                     </div>
                </div>
         </div>
         <br/>
         <br/>
      </div>
    )
  }

}

export default ProblemEditDialog;
