import React from 'react';
import { Redirect } from 'react-router-dom'
import '../css/DropdownMenu.css';


class DropdownMenuHover extends React.Component{
  constructor(props){
    super(props);
  }

  problemInsert=()=>{
    if(this.props.loggedInUser===''){
      this.props.setErrorMessage('Error: User not Logged In!')
    }
    else
      window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/problemInsert");
  }

  problemsBrowse=()=>{
    window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/problemsBrowse");
  }


  quizInsert=()=>{
    if(this.props.loggedInUser===''){
      this.props.setErrorMessage('Error: User not Logged In!')
    }
    else
      window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/quizInsert");
  }

  quizesBrowse=()=>{
    window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/quizesBrowse");
  }


  courseInsert=()=>{
    if(this.props.loggedInUser===''){
      this.props.setErrorMessage('Error: User not Logged In!')
    }
    else
      window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/courseInsert");
  }

  coursesBrowse=()=>{
    window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/coursesBrowse");
  }

  userInsert=()=>{
    if(this.props.loggedInUser!==''){
      alert('Error: User already registered and logged In !');
    }
    else
      window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/userInsert");
  }

  userShowProfile=()=>{
    window.location.assign("http://"+window.location.hostname+':'+window.location.port+
    "/userShowSelected/"+this.props.loggedInUser);
  }

  usersBrowse=()=>{
    window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/usersBrowse");
  }

  render(){
        return (
           <div>
            <fieldset className="fieldSet">
              <div className="dropdown dropdownProblem">
                <button className="problemMenuButton menuButton">Problem</button>
                <div id="problemMenu" className="problem-dropdown-content dropdown-content">
                  <a onClick={()=>this.problemInsert()}>Insert</a>
                  <a onClick={()=>this.problemsBrowse()}>Browse</a>
                </div>
              </div>

              <div className="dropdown dropdownQuiz">
                <button className="quizMenuButton  menuButton">Quiz</button>
                <div id="quizMenu" className="quiz-dropdown-content dropdown-content">
                  <a onClick={()=>this.quizInsert()}>Insert</a>
                  <a onClick={()=>this.quizesBrowse()}>Browse</a>
                </div>
              </div>

              <div className="dropdown dropdownCourse">
                <button className="courseMenuButton menuButton">Course</button>
                <div id="courseMenu" className="course-dropdown-content dropdown-content">
                  <a onClick={()=>this.courseInsert()}>Insert</a>
                  <a onClick={()=>this.coursesBrowse()}>Browse</a>
                </div>
              </div>

              <div className="dropdown dropdownUser">
                <button className="userMenuButton menuButton">User</button>
                <div id="userMenu" className="user-dropdown-content dropdown-content">
                  <a onClick={()=>this.userInsert()}>Register</a>
                  <a onClick={()=>this.usersBrowse()}>Browse</a>
                  {this.props.loggedInUser!==''?<a onClick={()=>this.userShowProfile()}>Profile</a>:null}
                </div>
              </div>
            </fieldset>
            </div>
        )
  }
}

export default DropdownMenuHover;
