import React from 'react';
import QuizEditDialog from './QuizEditDialog';
import {  Redirect } from 'react-router-dom';
import data from './data';

class QuizShowSelected extends React.Component{
  constructor(props){
    super(props);
    this.state={
      quizObj:{},
      problemsArray:[],
      showEditQuizDialog:false,
      quizObjForEdit:{},
      icons: data.icons
    }
    this.editQuizHandler=this.editQuizHandler.bind(this);
    this.handleEditQuizDialogDismiss=this.handleEditQuizDialogDismiss.bind(this);
    this.saveUpdateHandler=this.saveUpdateHandler.bind(this);
    this.descriptionChange=this.descriptionChange.bind(this);
    this.courseNameChange=this.courseNameChange.bind(this);
    this.selectChange=this.selectChange.bind(this);
    this.durationChange=this.durationChange.bind(this);
    this.AddQuizToCourseHandler=this.AddQuizToCourseHandler.bind(this);
  }

  AddQuizToCourseHandler(problemId){
    this.setState({showAddToDialog:true});
  }



  saveUpdateHandler(e){
    this.props.showSpinner();
    //console.log(newCourseObj);
    var reqBody="id="+encodeURIComponent(this.state.quizObjForEdit.id);
    reqBody+='&course_id='+encodeURIComponent(this.state.quizObjForEdit.course_id);
    reqBody+='&description='+encodeURIComponent(this.state.quizObjForEdit.description);
    reqBody+='&duration_minutes='+encodeURIComponent(this.state.quizObjForEdit.duration_minutes);

    fetch(`/api/updateQuiz`, {
      headers:{
        'Accept':'application/json',
        'Content-type': 'application/x-www-form-urlencoded'
      },
      method: 'POST',
      body: reqBody
    })
    .then(res=>res.json())
    .then(data=>{
                //console.log(data);

        if(data.updatestatus==="ok"){
            this.props.loadQuizes();
            let quizesArrayCopy=JSON.parse(JSON.stringify(this.props.quizesArray));
            quizesArrayCopy.forEach((obj)=>{
              if(obj.id===this.state.quizObjForEdit.id){
                 obj.courseName=this.state.quizObjForEdit.courseName;
                 obj.course_id=this.state.quizObjForEdit.course_id;
                 obj.description=this.state.quizObjForEdit.description;
                 obj.duration_minutes=this.state.quizObjForEdit.duration_minutes;
              }
            });
            this.props.setQuizes(quizesArrayCopy);
            this.setState({quizObj: this.state.quizObjForEdit, showEditQuizDialog:false});
            this.props.hideSpinner();
            alert('Quiz updated');
        }
        else{
          alert('Quiz updation failed!');
        }
    })
  }

  descriptionChange(e){
    const copied = Object.assign({}, this.state.quizObjForEdit)
    copied.description=e.target.value;
    this.setState({quizObjForEdit:copied});
  }

  courseNameChange(e){
    const copied = Object.assign({}, this.state.quizObjForEdit)
    copied.courseName=e.target.value;
    this.setState({quizObjForEdit:copied});
  }

  durationChange(e){
    const copied = Object.assign({}, this.state.quizObjForEdit)
    copied.duration_minutes=e.target.value;
    this.setState({quizObjForEdit:copied});
  }

  componentDidMount(){
    this.props.showSpinner();
    const {params}= this.props.match;

    var reqBody="quizId="+encodeURIComponent(params.id);
    fetch(`/api/getTheQuiz`, {
      headers:{
        'Accept':'application/json',
        'Content-type': 'application/x-www-form-urlencoded'
      },
      method: 'POST',
      body: reqBody
    })
    .then(res=>res.json())
    .then(data=>{
      data.id=params.id;
      //console.log(data);
      fetch(`/api/getProblemListForQuiz`, {
        headers:{
          'Accept':'application/json',
          'Content-type': 'application/x-www-form-urlencoded'
        },
        method: 'POST',
        body: reqBody
      })
      .then(res=>res.json())
      .then(data1=>{
            console.log(data1);
            this.setState({quizObj: data, quizObjForEdit: data, problemsArray: data1});
            this.props.hideSpinner();
       })
    })
  }

  editQuizHandler(){
    this.setState({showEditQuizDialog:true});
  }

  selectChange(e){
    const copied = Object.assign({}, this.state.quizObjForEdit)
    var new_course_id=e.target.value;
    var new_course_name=e.target.options[e.target.selectedIndex].text;
    copied.course_id=new_course_id;
    copied.courseName=new_course_name;
    //console.log(copied);
    this.setState({quizObjForEdit:copied});
  }


  deleteQuizHandler(id){
    let b=window.confirm('Are you sure you want to delete');
    if(b===true){
        this.props.showSpinner();
          var reqBody="id="+encodeURIComponent(id);
          fetch(`/api/deleteQuiz`, {
            headers:{
              'Accept':'application/json',
              'Content-type': 'application/x-www-form-urlencoded'
            },
            method: 'POST',
            body: reqBody
          })
          .then(res=>res.json())
          .then(data=>{
              if(data.deletestatus==="ok"){
                  let quizesArrayCopy=this.props.quizesArray.filter((obj)=>obj.id!==id);
                  this.props.setQuizes(quizesArrayCopy);
                  this.props.hideSpinner();
                  alert('Quiz deleted');
                  window.location.assign("http://"+window.location.hostname+':'+window.location.port+"/quizesBrowse");
              }
          })
    }//end of if
  }

  /* Toggle between showing and hiding the navigation menu links when the user clicks on the hamburger menu / bar icon */
 toggleNavDisplay() {
   var x = document.getElementById("TopNav");
  if (x.className === "TopNav") {
    x.className += " responsive";
  } else {
    x.className = "TopNav";
  }
}


  render(){
    if(this.props.loggedInUser===''){
      return <Redirect to="/errorLogin"/>
    }
    return (
      <div>
      <a class="HomeLink" href="/">
          <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
      {/*back to home*/}
      </a>
      <div class="h1">
        Quiz Details
      </div>
      <div className="Panel">
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
          <div className="h1">
            {this.state.quizObj.description}
          </div>
           <b>Course:</b><br/>
           <a href={`http://${window.location.hostname}:${window.location.port}/courseShowSelected/${this.state.quizObj.course_id}`}>
           {this.state.quizObj.courseName}
           </a>
           <br/><br/>
           <b>Duration (minutes):</b> {this.state.quizObj.duration_minutes}
           <div className="TopNavParent" style={{ width:'100%', textAlign:'right'}}>
               <div id="TopNav" className="TopNav">
                <a href="javascript:void(0);" class="HamIcon" onClick={()=>this.toggleNavDisplay()}>
                    <i class="fa fa-bars"></i>
                </a>
               <input type="button" className="LeftButton"
                onClick={()=>this.props.startQuiz(this.state.quizObj)}
                id={`s${this.state.quizObj.id}`} value="Start Quiz"/>
                <input type="button" className="LeftButton"
                 id={`a${this.state.quizObj.id}`} value="Add To Course"/>
               {(this.props.loggedInUser===this.state.quizObj.instructorId||this.props.admin==="1")?
               (<>
                  <input type="button"
                   className="LeftButton"
                   onClick={()=>this.editQuizHandler()}
                   id={`e${this.state.quizObj.id}`} value="Edit Quiz"/>
                  <input type="button" className="LeftButton"
                   onClick={()=>this.deleteQuizHandler(this.state.quizObj.id)}
                   id={`d${this.state.quizObj.id}`} value="Delete Quiz"/>
               </>):null}
                </div>
            </div>
           <br/>
          <b> Instructor:</b> {this.state.quizObj.instructorId}
          <br/><br/>
          {this.props.isQuizStarted(this.state.quizObj.id)?(
            <form>
            {(this.state.problemsArray.map(obj=>(
              <div>
                  <hr/>
                  <b>Question: </b>
                  <div className="Question"
                        dangerouslySetInnerHTML={{
                       __html: obj.description
                       }}
                  ></div><b>Options</b><br/>
                  <input type="radio" id={`${obj.id}$option1`} onChange={(e)=>this.props.optionChange(obj.id,e.target.value)}
                  checked={this.props.startedQuizAnswers!=null&&this.props.startedQuizAnswers[obj.id]==="1"}
                   name={`${obj.id}$`} value="1" ></input>
                  <span  dangerouslySetInnerHTML={{__html: obj.option1}}/><br/>
                  <input type="radio" id={`${obj.id}$option2`} onChange={(e)=>this.props.optionChange(obj.id,e.target.value)}
                  checked={this.props.startedQuizAnswers!=null&&this.props.startedQuizAnswers[obj.id]==="2"}
                   name={`${obj.id}$`} value="2"></input>
                  <span  dangerouslySetInnerHTML={{__html: obj.option2}}/><br/>
                  <input type="radio" id={`${obj.id}$option3`} onChange={(e)=>this.props.optionChange(obj.id,e.target.value)}
                    checked={this.props.startedQuizAnswers!=null&&this.props.startedQuizAnswers[obj.id]==="3"}
                   name={`${obj.id}$`} value="3"></input>
                  <span  dangerouslySetInnerHTML={{__html: obj.option3}}/><br/>
                  <input type="radio" id={`${obj.id}$option4`} onChange={(e)=>this.props.optionChange(obj.id,e.target.value)}
                    checked={this.props.startedQuizAnswers!=null&&this.props.startedQuizAnswers[obj.id]==="4"}
                   name={`${obj.id}$`} value="4"></input>
                  <span  dangerouslySetInnerHTML={{__html: obj.option4}}/><br/>
              </div>
            )
          ))}
           <br/>
           <input type="button" className="LeftButton"
           onClick={e=>{
                         if(window.confirm('Are you sure you want to submit the quiz:'+this.state.quizObj.description)===true)
                           this.props.submitQuiz();
                       }
                   }
           value="Submit Quiz"/>
          </form>
        ):null}
        <QuizEditDialog
                objectForEdit={this.state.quizObjForEdit}
                show={this.state.showEditQuizDialog}
                handleDismiss={this.handleEditQuizDialogDismiss}
                handleSubmit={this.saveUpdateHandler}
                descriptionChange={this.descriptionChange}
                courseNameChange={this.courseNameChange}
                loadCourses={this.props.loadCourses}
                coursesArray={this.props.coursesArray}
                selectChange={this.selectChange}
                durationChange={this.durationChange}
        />
        <AddToDialog
                show={this.state.showAddToDialog}
                handleDismiss={this.handleAddToDialogDismiss}
                />
     </div>
     </div>
    )
  }


}

export default QuizShowSelected;
