import React from 'react';

class QuizEditDialog extends React.Component{

  componentDidMount(){
    this.props.loadCourses();
  }


  constructor(props){
    super(props);
    this.state={
      searchKey: '',
      selectedCourseId:'',
      objectsFiltered:[]
    }

    this.searchBoxChange=this.searchBoxChange.bind(this);
    this.objectsFilteredClick=this.objectsFilteredClick.bind(this);
  }

  objectsFilteredClick(str, courseId, e){
    this.setState({searchKey: str, selectedCourseId: courseId, objectsFiltered: []})
  }



  searchBoxChange(e){
    const objectsFiltered=e.target.value!==''?this.props.coursesArray.filter((val)=>val.name.toUpperCase().startsWith(e.target.value.toUpperCase())):[];
    //const objectsFiltered=this.props.coursesArray.filter((val)=>val.description.toUpperCase().startsWith(e.target.value.toUpperCase()));
    this.setState({searchKey: e.target.value, objectsFiltered: objectsFiltered});
  }

  AddToCourseSubmit(e){
        const filteredArray=this.props.coursesArray.filter((val)=>val.name.toUpperCase()===this.state.searchKey.toUpperCase()&&val.id===this.state.selectedCourseId);
        if(filteredArray.length<1){
          alert('Search key does not match any available  \n\t quiz description');
        }
        else if(filteredArray.length>1){
          alert('Search key matches multiple quizes\' description!\n\t please choose another one');
        }
        else {
          console.log('filterd course object\n'+JSON.stringify(filteredArray[0]));
          this.props.addCourseToCoursesArraySubmit(filteredArray[0]);
          this.setState({searchKey:'', selectedCourseId:''})
        }
  }


  render(){
    //console.log(this.props.coursesArray);
    //console.log(this.props.objectForEdit);
    const visibilityClassName=this.props.show
    ? "simple-dialog-overlay simple-dialog-display-block"
    :"simple-dialog-overlay simple-dialog-display-none";

    return (
      <div className={visibilityClassName}>
         <div className="simple-dialog">
         <button id = "x" onClick={this.props.handleDismiss}>
          X
         </button>
           <h2>Edit Profile</h2>
           <hr class="rounded"/>
                          Quiz Id:<br/>
                          <input type="text" id="userId" style={{marginLeft:'23px'}}
                          value={this.props.objectForEdit.id}
                          readonly></input>
                          <br/><br/>
                          Duration (minutes):<br/>
                          <input type="text" id="durationMinutes" style={{marginLeft:'23px'}}
                          onChange={this.props.durationChange}
                          value={this.props.objectForEdit.duration_minutes}></input>
                          {/*<select id="courseId" onChange={this.props.selectChange}
                          style={{marginLeft:'23px'}}
                          name="courseId" required="true">
                              <option value="null">UnAssigned</option>
                          {
                              this.props.coursesArray.map(objQ=>(
                                <option value={objQ.id}>{objQ.name}</option>
                                )
                              )
                          }
                          </select>*/}
                          <br/><br/>
                         Description:<br/>
                         <textarea id="description" style={{marginLeft:'25px', width:'90%'}} type="text" rows="10"
                          onChange={this.props.descriptionChange}
                         value={this.props.objectForEdit.description}></textarea>
                         <br/><br/>
                         Courses:
                         <div id="AddToDialog">
                             <div className="autocomplete">
                                <input id="searchBox" type="text" name="searchBox"
                                value={this.state.searchKey}
                                onChange={this.searchBoxChange} placeholder="Type first letters of quiz "/>
                                <div className="autocomplete-items">
                                {
                                  this.state.objectsFiltered.map(obj=>(<div onClick={(e)=>this.objectsFilteredClick(obj.name, obj.id, e)}><strong>{obj.name.substring(0,this.state.searchKey.length)}</strong>{obj.name.substring(this.state.searchKey.length)}({obj.id})</div>))
                                }
                                </div>
                             </div>
                             <input type="submit" value="Add Course" onClick={(e)=>this.AddToCourseSubmit(e)}/>
                         </div>
                         <br/>
                         <div>
                         {
                             this.props.objectForEdit.coursesArray!==undefined?
                             ( this.props.objectForEdit.coursesArray.map(objQ=>(
                                 <div id={objQ.id} >|{objQ.name}
                                  <span className="chip" className="closeBtn"
                                    onClick={(e)=>this.props.removeCourseFromCoursesArray(e, objQ.id)}>&times;</span>{ }|
                                 </div>
                                 )
                               )
                             ):null
                         }
                         </div>
                         <br/><br/>
                         Creator:<br/>
                         <input type="text" id="creator" style={{marginLeft:'23px'}}
                         value={this.props.objectForEdit.instructorId}
                         readonly></input>
            <br/><br/><br/><br/><br/><br/>
           <input type="button" id="x" value="Save" style={{width:'100px', float: "right"}} onClick={this.props.handleSubmit}/>
           <br/><br/>
           <button id="x" style={{width:'100px'}}
             onClick={this.props.handleDismiss}>close
           </button>
         </div>
    </div>)
  }
}

export default QuizEditDialog;
