import React from 'react';
import ReactDOM from 'react-dom'
import data from './data';
import {  Redirect } from 'react-router-dom';

class MeetingInsert extends React.Component{

  constructor(props){
    super(props);
    this.state={
      description:'',
      spinner:false,
      icons: data.icons
    }
  }

  render(){
    if(this.props.loggedInUser===''){
      return <Redirect to="/errorLogin"/>
    }

    return (
      <div className="meetingInsert">
          <a class="HomeLink" href="/">
              <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
          {/*back to home*/}
          </a>
          <div class="h1">
            Insert Meeting
          </div>
          <fieldset>
            <br/>
            Meeting Description:<br/>
            <textarea id="meetingDescription" name="meetingDescription" type="text" rows="10"
            style={{width:'90%'}}
            onChange={this.descriptionChange}
            value={this.state.description}
             >
            </textarea>
            <br/><br/>
            Author:<br/>
            <input id="authorName" name="authorName" type="text" style={{width:'100px', textAlign: 'center'}}
            required="true" value={this.props.loggedInUser} readonly>
            </input>
            <br/>
            <br/>
          </fieldset>
      </div>
    )
   }
}

export default MeetingInsert;
