import React from 'react';
import ProblemEditDialog from './ProblemEditDialog';
import {  Redirect } from 'react-router-dom';

class ProblemShowSelected extends React.Component{
  constructor(props){
    super(props);
    this.state={
      problemObj:{},
      showEditProblemDialog:false,
      problemObjForEdit:{}
    }
    this.editProblemHandler=this.editProblemHandler.bind(this);
    this.handleEditProblemDialogDismiss=this.handleEditProblemDialogDismiss.bind(this);
    this.saveUpdateHandler=this.saveUpdateHandler.bind(this);
    this.descriptionChange=this.descriptionChange.bind(this);
    this.quizDescriptionChange=this.quizDescriptionChange.bind(this);
    this.selectChange=this.selectChange.bind(this);
  }

  componentDidMount(){
     this.props.showSpinner();
     const {params}= this.props.match;

     var reqBody="problemId="+encodeURIComponent(params.id);
     fetch(`/api/getTheProblem`, {
       headers:{
         'Accept':'application/json',
         'Content-type': 'application/x-www-form-urlencoded'
       },
       method: 'POST',
       body: reqBody
     })
     .then(res=>res.json())
     .then(data=>{
        console.log(data);
        this.setState({problemObj: data});
      })
  }

  selectChange(){

  }

  quizDescriptionChange(){

  }

  descriptionChange(){

  }

  saveUpdateHandler(){

  }

  handleEditProblemDialogDismiss(){

  }

  editProblemHandler(problemObj){

  }

  deleteProblemHandler(id){

  }

  showAnswerHandler(divId){
      if(document.getElementById(divId).style.display!=='block'){
      // document.getElementById(answerId).style.visibility='hidden';
        document.getElementById(divId).style.display='block';
      }
      else  if(document.getElementById(divId).style.display==='block'){
        document.getElementById(divId).style.display='none';
      }
  }

  render(){
    return (
      <div className="Panel">
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
          <a className="HomeLink" href="/">back to home</a>
          <div className="h1">
            Problem Details
          </div>
          <div className="row">
              <div id="flexColLeft">
              {(this.props.loggedInUser===this.state.problemObj.author_id||this.props.admin==="1")?
              (<div>
                <input type="button" className="EditButton"
                onClick={()=>this.deleteProblemHandler(this.state.problemObj.id)}
                id={`delete${this.state.problemObj.id}`} value="Delete Problem"/>
              <input type="button" className="EditButton"
                onClick={()=>this.editProblemHandler(this.state.problemObj)}
                id={`e${this.state.problemObj.id}`} value="Edit Problem"/></div>)
              :null}
                <b>Quiz: </b>
                <div id={`quizDescription$,${this.state.problemObj.id}`} className="Quiz">
                {this.state.problemObj.quiz_description}
                </div>
              <br/><br/>
              <br/><b>Author</b> : <label id={`authorId$,${this.state.problemObj.id}`}>{this.state.problemObj.author_id}</label>
                Problem Description:<br/>
                <div id={`problemDescription$,${this.state.problemObj.id}`}
                dangerouslySetInnerHTML={{__html: this.state.problemObj.description}}
                className="Question"></div>
                <br/><br/>
                <b>Options:</b>
                <br/> 1) <label id={`option1$,${this.state.problemObj.id}`}>{this.state.problemObj.option1}</label>
                <br/> 2) <label id={`option2$,${this.state.problemObj.id}`}>{this.state.problemObj.option2}</label>
                <br/> 3) <label id={`option3$,${this.state.problemObj.id}`}>{this.state.problemObj.option3}</label>
                <br/> 4) <label id={`option4$,${this.state.problemObj.id}`}>{this.state.problemObj.option4}</label>
                <br/><br/>
                <input type="button" className="showAnswer" onClick={()=>this.showAnswerHandler(`d${this.state.problemObj.id}`)} id={`b${this.state.problemObj.id}`} value="view solution">
                </input>
                <br/>

                  Answer Description:<br/>
                  <textarea id="ansDescription" name="ansDescription" type="text" rows="10"
                  value={obj.solution}
                  onChange={this.props.solutionChange}
                  style={{width:'90%'}}
                  required="true">
                  </textarea>
              </div>
              <div id="flexColRight">
              <br/><br/><br/>
                   Option1: <br/>
                   <input type="text" style={{width:'50%'}}
                              onChange={this.props.option1Change}
                              id="option1" value={obj.option1}/><br/>
                   Option2: <br/>
                   <input type="text" style={{width:'50%'}}
                            onChange={this.props.option2Change}
                            id="option2" value={obj.option2}/><br/>
                   Option3: <br/>
                   <input type="text" style={{width:'50%'}}
                            onChange={this.props.option3Change}
                            id="option3" value={obj.option3}/><br/>
                   Option4: <br/>
                   <input type="text" style={{width:'50%'}}
                              onChange={this.props.option4Change}
                              id="option4" value={obj.option4}/><br/>
                   Answer Key: <br/>
                   <input type="text" id="answerKey"
                   onChange={this.props.answerkeyChange}
                   style={{width:'50%'}} value={obj.answerkey}/>
                   <br/>
                   <br/>
                   Author:<br/>
                   <input id="authorName" name="authorName" type="text" style={{width:'80%'}}
                   required="true" value={obj.author_id} readonly>
                   </input> <br/><br/><br/><br/>
                  <input type="button" id="x" value="Save" style={{width:'80%', float: "right"}}
                  onClick={this.props.handleSubmit}/><br/><br/>
                  <button id="x" style={{width:'80%', textAlign:'center'}}
                   onClick={this.props.handleDismiss}>close
                  </button>
              </div>
         </div>
      </div>
    )
  }

}


export default ProblemShowSelected;
