import React from 'react';
import '../css/Browse.css';
import EditProblemDialog from './ProblemEditDialog';

class ProblemsBrowse extends React.Component{


  render(){
    return (
      <div>
          <a class="HomeLink" href="/">back to home</a>
          <div class="h1">
            Browse Problems
          </div>
          {
           this.props.problemsArray.map(obj=>(
            <div id={`par$,${obj.id}`} className="probParent">
                <hr />
                {/*{(this.props.loggedInUser===obj.author_id||this.props.admin==="1")?
                (<div>
                  <input type="button" className="EditButton"
                  onClick={()=>this.deleteProblemHandler(obj.id)}
                  id={`delete${obj.id}`} value="Delete Problem"/>
                <input type="button" className="EditButton"
                  onClick={()=>this.editProblemHandler(obj)}
                  id={`e${obj.id}`} value="Edit Problem"/></div>)
                :null}*/}
                <b>Problem Description: </b>
                <a href={`./problemShowSelected/${obj.id}`}>
                    <div id={`problemDescription$,${obj.id}`}
                    dangerouslySetInnerHTML={{__html: obj.description}}
                    className="Question"></div>
                </a>
                <br/>
                <b>Quiz: </b>
                <a href={`http://${window.location.hostname}:${window.location.port}/quizShowSelected/${obj.quiz_id}`}>
                  <div id={`quizDescription$,${obj.id}`} style={{marginLeft:'30px'}} className="Quiz">
                  {obj.quiz_description}
                  </div>
                </a>
                <input type="hidden" id={`quizId$,'${obj.id}`} value={obj.quiz_id}/>
                <br/><b>Author</b> : <label id={`authorId$,${obj.id}`}>{obj.author_id}</label>
                  {/*<b>Options:</b>
                  <br/> 1) <label id={`option1$,${obj.id}`}>{obj.option1}</label>
                  <br/> 2) <label id={`option2$,${obj.id}`}>{obj.option2}</label>
                  <br/> 3) <label id={`option3$,${obj.id}`}>{obj.option3}</label>
                  <br/> 4) <label id={`option4$,${obj.id}`}>{obj.option4}</label>
                  <br/><br/>
                  <input type="button" className="showAnswer" onClick={()=>this.showAnswerHandler(`d${obj.id}`)} id={`b${obj.id}`} value="view solution">
                  </input>
                  <br/>
                  <div id={`d${obj.id}`} name={`d${obj.id}`} className="Answer"><b>Solution: </b>
                      <label id={`solution$,${obj.id}`}
                        dangerouslySetInnerHTML={{__html: obj.solution}}
                      ></label><br/>
                      Ans Key: <label id={`answerkey$,${obj.id}`}>{obj.answerkey}</label>
                  </div>*/}
              </div>
              )
            )
          }
     </div>
    )
  }



  componentDidMount(){
    this.props.loadProblems();
    this.props.loadQuizes();
  }


  constructor(props){
    super(props);
    this.state={
      problemObjectForEdit:{},
      showEditProblemDialog:false
    }
  }
}

export default ProblemsBrowse;
