import React from 'react';
import './css/Middle.css';

class Middle extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return (
      <div class="Middle">
        <div class="Mandela-quote">
          <footer>
            <cite>
               Nelson Mandela
            </cite>
          </footer>
          <p>
          “Education is the most powerful weapon which you can use to change the world.”
          </p>
        </div>
      </div>
    )
  }
}

export default Middle;
