import React from 'react';
import data from './data';

class CoursesBrowse extends React.Component{

  componentDidMount(){
    this.props.loadCourses();
  }

  render(){
    return (
      <div>
          <a class="HomeLink" href="/">
              <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
          {/*back to home*/}
          </a>
          <div class="h1">
            Browse Courses
          </div>
          <br/>
          <div className="pagination">
           <a href="#" onClick={(e)=>{this.props.prevPageClick(e)}}>&laquo;</a>
            {
              this.props.pageArray.map(val=><a onClick={(e)=>{this.props.setCoursesPage(val)}} className={val===this.props.currentPage?"active":""} href="#">{val}</a>)
            }
           <a href="#" onClick={(e)=>this.props.nextPageClick(e)}>&raquo;</a>
          </div>
          <br/><br/>
         <table className="displayTable" style={{width:"100%"}}>
           <tr>
              <th>Course name</th><th>Description</th><th>Owner</th>
           </tr>
           {
             this.props.coursesArray.map(obj=>(
               <tr>
                 <td><a href={`./courseShowSelected/${obj.id}`}>{obj.name}</a></td>
                 <td>{obj.description}</td>
                 <td>{obj.author_id}</td>
               </tr>
               )
             )
           }
         </table>
     </div>

    )
  }

  constructor(props){
    super(props);
    this.state={
      spinner:false,
      objArray:[],
      icons: data.icons
    }
  }
}

export default CoursesBrowse;
