// A wrapper for <Route> that redirects to the login
// screen if you're not yet authenticated.
function PrivateRoute({ children, loggedInUser, ...rest }) {
  return (
    <Route
      {...rest}
      render={({ location }) =>
        loggedInUser!=="" ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: "/errorLogin",
              state: { from: location }
            }}
          />
        )
      }
    />
  );
}



