import React from 'react';

class QuizesBrowse extends React.Component{
  componentDidMount(){
    this.props.loadQuizes();
    /*this.setState({spinner:true});
    fetch(`/api/getQuizes`, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      this.setState({spinner:false, objArray: data});
    })*/
  }

  render(){
    return (
      <div>
          {/*
            this.state.spinner===true?(<div id="spinner" className="container"><div className="loading"></div></div>):null
          */}
          <a class="HomeLink" href="/">back to home</a>
          <div class="h1">
            Browse Quizes
          </div>
          <div  className='Browse'>
               <table className="displayTable" style={{width:"100%"}}>
                 <tr>
                   <th>Quiz</th><th>Duration(mins)</th><th>Course</th><th>Instructor</th>
                 </tr>
                 {
                   this.props.quizesArray.map(obj=>(
                     <tr>
                       <td><a href={`./quizShowSelected/${obj.id}`}>{obj.description}</a></td>
                       <td>{obj.duration_minutes}</td>
                       <td>{obj.name}</td>
                       <td>{obj.author_id}</td>
                     </tr>
                     )
                   )
                 }
               </table>
         </div>
     </div>
    )
  }

  constructor(props){
    super(props);
    this.state={
      spinner:false
    }
  }
}

export default QuizesBrowse;
