import React from 'react';
import data from './data';

class MeetingsBrowse extends React.Component{
  componentDidMount(){
    this.props.loadMeetings();
  }

  render(){
    return (
      <div>
          <a class="HomeLink" href="/">
              <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
          {/*back to home*/}
          </a>
          <div class="h1">
            Browse Meetings
          </div>
          <br/>
          <div className="pagination">
           <a href="#" onClick={(e)=>{this.props.prevPageClick(e)}}>&laquo;</a>
            {
              this.props.pageArray.map(val=><a onClick={(e)=>{this.props.setMeetingsPage(val)}} className={val===this.props.currentPage?"active":""} href="#">{val}</a>)
            }
           <a href="#" onClick={(e)=>this.props.nextPageClick(e)}>&raquo;</a>
          </div>
          <br/><br/>
          <div  className='Browse'>
               <table className="displayTable" style={{width:"100%"}}>
                 <tr>
                   <th>Meeting</th><th>Organiser</th>
                 </tr>
                 {
                   this.props.meetingsArray.map(obj=>(
                     <tr>
                       <td><a href={`./meetingShowSelected/${obj.id}`}>{obj.description}</a></td>
                       <td>{obj.organiser_id}</td>
                     </tr>
                     )
                   )
                 }
               </table>
         </div>
         <div className="pagination">
          <a href="#" onClick={(e)=>{this.props.prevPageClick(e)}}>&laquo;</a>
           {
             this.props.pageArray.map(val=><a onClick={(e)=>{this.props.setMeetingsPage(val)}} className={val===this.props.currentPage?"active":""} href="#">{val}</a>)
           }
          <a href="#" onClick={(e)=>this.props.nextPageClick(e)}>&raquo;</a>
         </div>
     </div>
    )
  }

  constructor(props){
    super(props);
    this.state={
      spinner:false,
      icons: data.icons
    }
  }
}

export default MeetingsBrowse;
