import React from 'react';

class AddToDialog extends React.Component{

  constructor(props){
    super(props);
    this.state={
      searchInput:''
    }
    this.searchBoxChange=this.searchBoxChange.bind(this);
  }

 searchBoxChange(e){
   this.setState({searchInput:e.target.value})
 }

  render(){
    const visibilityClassName=this.props.show
    ? "simple-dialog-overlay simple-dialog-display-block"
    : "simple-dialog-overlay simple-dialog-display-none";

    return (
        <div className={visibilityClassName}>
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"/>
           <div className="simple-dialog" id="ProblemAddToDialog">
               <button id = "x" onClick={this.props.handleDismiss}>
                X
               </button>
               <h2>Add Problem To Quiz</h2>
               <hr class="rounded"/>
               <div className="row">
               <input type="text" style={{width:'50%'}} placeholder="Search.." name="searchBox" className="SearchBox"
                          onChange={this.searchBoxChange}
                          id="searchBox" value={this.state.searchInput}/>
               <button><i class="fa fa-search"></i></button>
               </div>
            </div>
        </div>
    )
  }
}

export default AddToDialog;
