import React from 'react';
import '../css/Browse.css';

class UsersBrowse extends React.Component{
  componentDidMount(){
    /*this.setState({spinner:true});
    fetch(`/api/getUsers`, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      this.setState({spinner:false, objArray: data});
    })*/
    this.props.loadUsers();
  }


  render(){
    return (
      <div>
          <a className="HomeLink" href="/">back to home</a>
          <div className="h1">
            Browse Users
          </div>
          <div  className='Browse'>
          <table className="displayTable">
           <tr>
             <th>id</th><th>full name</th><th>address</th><th>city</th><th>zip</th><th>phone</th><th>mobile</th><th>email</th>
           </tr>
             {
               this.props.usersArray.map(obj=>(
                 <tr>
                   <td><a href={`./userShowSelected/${obj.id}`}>{obj.id}</a></td>
                   <td>{obj.first_name+' '+obj.last_name}</td>
                   <td>{obj.address1+' '+obj.address2}</td>
                   <td>{obj.city}</td><td>{obj.zip}</td>
                   <td>{obj.phone}</td><td>{obj.mobile}</td>
                   <td>{obj.email}</td>
                 </tr>
                 )
               )
             }

         </table>
         </div>
     </div>
    )
  }


}

export default UsersBrowse;
