import React, {Component} from 'react';
import FacebookLogin from 'react-facebook-login';
import GoogleLogin from 'react-google-login';
import configurationLogins from '../ConfigurationLogins'
import {getMinutesRemaining} from '../utils/utils';
import data from './data';

class Header extends Component{
  constructor(props){
    super(props);
    this.state={
      userId:'',
      password:'',
      firstName:'',
      lastName:'',
      address1:'',
      address2:'',
      city:'',
      zip:'',
      phone:'',
      cell:'',
      email:'',
      icons: data.icons
    }
  }

  render(){
        let globalState=this.props.state;
        //console.log(this.props.loggedInUser);
        return (this.props.loggedInUser===""?(
              <div className="Header">
                <form method="post" action="login" onSubmit={this.props.handleSubmit} noValidate>
                <div className="row">
                      <div id="flexColLeft">
                        <span id="logo">Schools.com</span><br/>
                        {globalState.startedQuiz!==null?
                                                (
                                                  <label style={{color:'maroon', textDecoration:'underline', cursor: 'pointer' }}
                                                  onClick={(e)=>window.location.assign("https://"+window.location.hostname+':'+window.location.port+"/quizShowSelected/"+this.props.startedQuiz.id)}>
                                                  <br/>{globalState.startedQuiz.description} started ({getMinutesRemaining(globalState.startedQuiz.start_time, globalState.startedQuiz.duration_minutes)} mins left) </label>
                                                 ):null}
                        <label style={{color:"red", paddingRight:"50px"}}>{this.props.errorMessage}</label>
                      </div>
                      <div id="flexColRight">
                              <a  id="HeaderRightLink" href='/userInsert'>Register User</a><br/><br/><br/>
                              <label for="userId">user id</label>
                              <input type="text" id="userName" name="userName" tabindex="1"
                                    onChange={this.props.userIdChange} value={this.props.userId}
                               ></input>
                               <label for="password">password</label>
                               <input type="password" id="password" name="password" tabindex="2"
                                  onChange={this.props.passwordChange} value={this.props.password}
                               ></input>
                               <input type="submit" value="Login" tabindex="3"/><br/><br/>
                               <div class='alt-login-container'>
                               <GoogleLogin className='alt-login-component'
                                style={{fontSize:'10pt'}}
                                className='alt-login-component'
                                clientId={configurationLogins.google_client_id} //CLIENTID NOT CREATED YET
                                buttonText="LOGIN WITH GOOGLE"
                                onSuccess={(response)=>this.props.handleAltLogin("google",response)}
                                onFailure={(response)=>this.props.handleAltLogin("google",response)}
                                />
                                <FacebookLogin
                                //className='alt-login-component'
                                 cssClass='alt-login-component alt-login-component-fb'
                                 icon={this.state.icons.facebook}
                                 appId={configurationLogins.facebook_api_key} //APP ID NOT CREATED YET
                                 disableMobileRedirect={true}
                                 fields="name,email,picture"
                                 callback={(response)=>this.props.handleAltLogin("facebook",response)}
                                 />
                              </div>
                      </div>
                </div>
                </form>
              </div>
        ):
        (
          <div className="Header" noValidate>
          <form method="post">
              <div className="row">
                    <div id="flexColLeft">
                        <span id="logo">Schools.com</span>
                        {globalState.startedQuiz!==null?
                                                (
                                                  <label style={{color:'maroon', textDecoration:'underline', cursor: 'pointer' }}
                                                  onClick={(e)=>window.location.assign("https://"+window.location.hostname+':'+window.location.port+"/quizShowSelected/"+this.props.startedQuiz.id)}>
                                                  <br/>{globalState.startedQuiz.description} started ({getMinutesRemaining(this.props.startedQuiz.start_time, globalState.startedQuiz.duration_minutes)} mins left) </label>
                                                 ):null}
                    </div>
                    <div id="flexColRight">
                        <span id="loginStatus">
                            <a id="HeaderRightLink" href="/" style={{textDecoration:"underline"}} onClick={this.props.logoutUser}>Logout</a>
                            <br/><br/><br/>
                            <label>Logged in as: </label>
                            <label id="userId">
                            {this.props.loggedInUserFullName}
                            </label>
                        </span>
                    </div>
              </div>
          </form>
          </div>
        ))
  }

}

export default Header;
