import React from 'react';
import '../css/Browse.css';
import data from './data';
import {getPageArray} from '../utils/utils';

class ProblemsBrowse extends React.Component{

  componentDidMount(){
    this.props.loadProblems();
    this.props.loadQuizes();
    this.setState({pageArray: getPageArray(this.state.currentPage, this.props.problemsArray===undefined||this.props.problemsArray.length<this.state.pageSize)});
    console.log(getPageArray(this.state.currentPage, this.props.problemsArray===undefined||this.props.problemsArray.length<this.state.pageSize));

  }


  constructor(props){
    super(props);
    this.state={
      problemObjectForEdit:{},
      showEditProblemDialog:false,
      icons: data.icons,
      currentPage:1,
      pageSize:20
    }
    this.nextPageClick=this.nextPageClick.bind(this);
    this.prevPageClick=this.prevPageClick.bind(this);
  }

  nextPageClick(e){
    let pageArray=getPageArray(this.state.currentPage, this.props.problemsArray===undefined||this.props.problemsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentPage+1))
        this.setState({currentPage:this.state.currentPage+1});
  }

  prevPageClick(e){
    let pageArray=getPageArray(this.state.currentPage, this.props.problemsArray===undefined||this.props.problemsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentPage-1))
        this.setState({currentPage:this.state.currentPage-1});
  }

  render(){

    let pageArray=getPageArray(this.state.currentPage, this.props.problemsArray===undefined||this.props.problemsArray.length<this.state.pageSize);

    return (
      <div>
      <a class="HomeLink" href="/">
          <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
      {/*back to home*/}
      </a>
          <div class="h1">
            Browse Problems
          </div>
          <div className="pagination">
           <a href="#" onClick={(e)=>this.prevPageClick(e)}>&laquo;</a>
            {
              pageArray.map(val=><a onClick={(e)=>{this.setState({currentPage:val})}} className={val===this.state.currentPage?"active":""} href="#">{val}</a>)
            }
           <a href="#" onClick={(e)=>this.nextPageClick(e)}>&raquo;</a>
          </div> <br/><br/>
          {
           this.props.problemsArray.map(obj=>(
            <div id={`par$,${obj.id}`} className="probParent">
                <hr />
                <b>Problem Description: </b>
                <a href={`./problemShowSelected/${obj.id}`}>
                    <div id={`problemDescription$,${obj.id}`}
                    dangerouslySetInnerHTML={{__html: obj.description}}
                    className="Question"></div>
                </a>
                <br/>
                <b>Author: </b>
                <a href={`http://${window.location.hostname}:${window.location.port}/userShowSelected/${obj.author_id}`}>
                  <div id={`author$,${obj.id}`} style={{marginLeft:'30px'}} className="Quiz">
                  {obj.author_id}
                  </div>
                </a>
                <input type="hidden" id={`quizId$,'${obj.id}`} value={obj.quiz_id}/>
              </div>
              )
            )
          }
     </div>
    )
  }



}

export default ProblemsBrowse;
