import React from 'react';
import {  Redirect } from 'react-router-dom';
import data from './data';

class UserInsert extends React.Component{

  constructor(props){
    super(props);
    this.state={
      spinner:false,
      userId:'',
      password:'',
      firstName:'',
      lastName:'',
      address1:'',
      address2:'',
      city:'',
      zip:'',
      phone:'',
      cell:'',
      email:'',
      sex_male: true,
      profile_image_url:null,
      avatar: data.avatar,
      icons: data.icons
    }
    this.baseState=this.state;
    this.userIdChange=this.userIdChange.bind(this);
    this.passwordChange=this.passwordChange.bind(this);
    this.firstNameChange=this.firstNameChange.bind(this);
    this.lastNameChange=this.lastNameChange.bind(this);
    this.address1Change=this.address1Change.bind(this);
    this.address2Change=this.address2Change.bind(this);
    this.cityChange=this.cityChange.bind(this);
    this.zipChange=this.zipChange.bind(this);
    this.phoneChange=this.phoneChange.bind(this);
    this.cellChange=this.cellChange.bind(this);
    this.emailChange=this.emailChange.bind(this);
    this.handleSubmit=this.handleSubmit.bind(this);
  }

  handleSubmit(e){
    if(this.state.userId===''||this.state.password===''){
      alert('UserId/Password cannot be empty');
      return;
    }
    else{
      this.props.showSpinner();
      var reqBody='userId='+encodeURIComponent(this.state.userId);
      reqBody+='&password='+encodeURIComponent(this.state.password);
      reqBody+='&firstName='+encodeURIComponent(this.state.firstName);
      reqBody+='&lastName='+encodeURIComponent(this.state.lastName);
      reqBody+='&address1='+encodeURIComponent(this.state.address1);
      reqBody+='&address2='+encodeURIComponent(this.state.address2);
      reqBody+='&city='+encodeURIComponent(this.state.city);
      reqBody+='&zip='+encodeURIComponent(this.state.zip);
      reqBody+='&phone='+encodeURIComponent(this.state.phone);
      reqBody+='&cell='+encodeURIComponent(this.state.cell);
      reqBody+='&email='+encodeURIComponent(this.state.email);

      fetch(`/api/insertUserAction`, {
        headers:{
          'Accept':'application/json',
          'Content-type': 'application/x-www-form-urlencoded'
        },
        method: 'POST',
        body: reqBody
      })
      .then(res=>res.json())
      .then(data=>{
          //if(data.updatestatus=="ok")
              let usersArrayCopy=[...this.props.usersArray];
              let obj={
                id:this.state.userId,
                password:this.state.password,
                first_name: this.state.firstName,
                last_name: this.state.lastName,
                address1: this.state.address1,
                address2: this.state.address2,
                city:this.state.city,
                zip:this.state.zip,
                phone:this.state.phone,
                mobile:this.state.cell,
                email:this.state.email,
                profile_image_url: this.state.profile_image_url,
                sex_male: this.state.sex_male
              };
              usersArrayCopy.push(obj);
              this.props.setUsers(usersArrayCopy);
              this.setState(this.baseState);
              this.props.hideSpinner();
              alert('User inserted');
              window.location.assign("http://"+window.location.hostname+':'+window.location.port);
      })

    }
  }

  emailChange(e){
    this.setState({email:e.target.value})
  }

  cellChange(e){
    this.setState({cell:e.target.value})
  }

  phoneChange(e){
    this.setState({phone:e.target.value})
  }

  zipChange(e){
    this.setState({zip:e.target.value})
  }

  cityChange(e){
    this.setState({city:e.target.value})
  }

  address2Change(e){
    this.setState({address2:e.target.value})
  }

  address1Change(e){
    this.setState({address1:e.target.value})
  }

  lastNameChange(e){
    this.setState({lastName:e.target.value})
  }

  firstNameChange(e){
    this.setState({firstName:e.target.value})
  }

  passwordChange(e){
    this.setState({password:e.target.value})
  }

  userIdChange(e){
    this.setState({userId:e.target.value})
  }

  profileImageChange(e){

  }

  render(){
    if(this.props.loggedInUser===''){
      return <Redirect to="/errorLogin"/>
    }
    return (
      <div id="UserInsert">
      <a class="HomeLink" href="/">
          <img className="homeIcon" src={this.state.icons.home} alt="back to home"/>
          {/*back to home*/}
      </a>
          <div class="h1">
          Register User
          </div>
          <br/>
          <fieldset>
          <br/><br/>
          <div className="profileParent" style={{width:'100%', textAlign:'center'}}>
              <div className="profileCard">
                <label htmlFor='ProfileUpload'>
                <img src={this.state.profile_image_url!=null?this.state.profile_image_url: ( this.state.sex_male==true?this.state.avatar.male:this.state.avatar.female)} alt="Avatar"/>
                <div className="profileCardOverlay">{this.state.profile_image_url!=null?"Update Image": "Upload Image"}
                </div>
               </label>
               <input type='file' id='ProfileUpload' onChange={this.props.profileImageChange} />
              </div>
          </div>
            <div className="row">
                <div id="flexColLeft">
                        <br/>User Id:
                        <input id="userId" name="userId" style={{marginLeft:'0.5cm'}} type="text"
                        value={this.state.userId}
                        onChange={this.userIdChange}>
                        </input> <br/><br/>
                        Password:
                        <input id="password" name="password" type="password"
                        value={this.state.password}
                        onChange={this.passwordChange}>
                        </input><br/><br/>
                        First Name:
                        <input id="firstName" name="firstName" type="text" rows="10" size="18"
                        value={this.state.firstName}
                        onChange={this.firstNameChange}
                        ></input><br/><br/>
                        Last Name:
                        <input id="lastName" name="lastName" type="text" rows="10" size="19"
                        value={this.state.lastName}
                        onChange={this.lastNameChange}>
                        </input><br/><br/>
                        Phone:
                        <input id="phone" name="phone" style={{marginLeft:'0.5cm'}} type="text"
                        value={this.state.phone}
                        onChange={this.phoneChange}
                        ></input><br/><br/>
                          Cell num:
                         <input id="cell" name="cell" type="text"
                         value={this.state.cell}
                         onChange={this.cellChange}
                         ></input>
                </div> {/*end of flexColLeft*/}
                <div id="flexColRight">
                       <br/>Address1:
                       <input id="address1" name="address1" type="text"
                       value={this.state.address1}
                       onChange={this.address1Change}
                       ></input><br/><br/>
                       Address2:
                      <input id="address2" name="address2" type="text"
                      value={this.state.address2}
                      onChange={this.address2Change}
                        ></input><br/><br/>
                      City:
                      <input style={{marginLeft:'1cm'}} id="city" name="city" type="text"
                      value={this.state.city}
                      onChange={this.cityChange}
                       ></input><br/><br/>
                      Zip:
                      <input style={{marginLeft:'1.2cm'}} id="zip" name="zip" type="text"
                      value={this.state.zip}
                      onChange={this.zipChange}
                      ></input><br/><br/>
                       Email:
                       <input style={{marginLeft:'1cm'}} id="email" name="email" type="text"
                       value={this.state.email}
                       onChange={this.emailChange}
                        >
                       </input><br/><br/>
                        <input type="submit" style={{width: '20%'}} value="submit"
                        onClick={this.handleSubmit}/>
                                      </div> {/*end of flexColRight*/}
                                  </div>{/*end of row*/}
          </fieldset>
      </div>
    )
  }

}

export default UserInsert;
