import React from 'react';
import '../css/Browse.css';
import data from './data';

class ProblemSearch extends React.Component{

  componentDidMount(){
  }

  constructor(props){
    super(props);
    this.state={
      searchKey:'',
      icons: data.icons
    }
    this.searchKeyChange=this.searchKeyChange.bind(this);
  }

  searchKeyChange(e){
    this.setState({searchKey: e.target.value});
  }

  render(){
    return (
      <div>
      <a class="HomeLink" href="/">
          <img class="homeIcon" src={this.state.icons.home} alt="back to home"/>
      {/*back to home*/}
      </a>
          <div class="h1">
            Search Problems
          </div>
          <fieldset>
          <input type="text" id="searchKey" style={{marginLeft:'23px'}}
          onChange={this.searchKeyChange}
          value={this.state.searchKey}></input>
          </fieldset>
      </div>
    )
  }

}

export default ProblemSearch;
