import React from 'react';
import '../css/Middle.css';

class Middle extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return (
      <div class="Middle">
         <div class="Mandela-flex">
               <div class="k12Div">
                 <h3 class="k12h3">K-12 <br/>Students</h3>
                 <p>The new generation of schoolchildren is not only digitally adept, but also highly motivated to use online learning tools.</p>
               </div>
               <div class="Mandela-quote">
                  <cite>
                     Nelson Mandela
                  </cite>
                  <p>
                      “Education is the most powerful weapon which you can use to change the world.”
                  </p>
               </div>
               <div class="howToStudyOnline">
                   <a class="elementor-post__thumbnail__link" href="https://e-student.org/how-to-study-online/">
                      <h3>How to Study Online: Tips & Tricks</h3>
                   </a>
               </div>
         </div>
         <div>
            <div>
              <img src="https://e-student.org/wp-content/uploads/2020/02/target-groups-of-corporate-e-learning-pie-graph.jpg" class="attachment-large size-large lazyloaded" alt="" sizes="(max-width: 800px) 100vw, 800px" srcset="https://e-student.org/wp-content/uploads/2020/02/target-groups-of-corporate-e-learning-pie-graph.jpg 800w, https://e-student.org/wp-content/uploads/2020/02/target-groups-of-corporate-e-learning-pie-graph-300x145.jpg 300w, https://e-student.org/wp-content/uploads/2020/02/target-groups-of-corporate-e-learning-pie-graph-768x372.jpg 768w" data-ll-status="loaded" width="800" height="387"/>
            </div>
         </div>
      </div>
    )
  }
}

export default Middle;
