import React from 'react';
import './css/App.css';
import Header from './components/Header';
import ImageSlider from './components/ImageSlider';
import Middle from './components/Middle';
import DropdownMenuHover from './components/DropdownMenuHover';
import ProblemsBrowse from './components/ProblemsBrowse';
import ProblemsSearch from './components/ProblemsSearch';
import ProblemInsert from './components/ProblemInsert';
import ProblemShowSelected from './components/ProblemShowSelected';
import QuizesBrowse from './components/QuizesBrowse';
import QuizesSearch from './components/QuizesSearch';
import QuizInsert from './components/QuizInsert';
import QuizShowSelected from './components/QuizShowSelected';
import QuizInstances from './components/QuizInstances';
import UsersBrowse from './components/UsersBrowse';
import UsersSearch from './components/UsersSearch';
import UserInsert from './components/UserInsert';
import UserShowSelected from './components/UserShowSelected';
import UserQuizInstances from './components/UserQuizInstances';
import CoursesBrowse from './components/CoursesBrowse';
import CourseInsert from './components/CourseInsert';
import CoursesSearch from './components/CoursesSearch';
import CourseShowSelected from './components/CourseShowSelected';
import MeetingInsert from './components/MeetingInsert';
import MeetingsBrowse from './components/MeetingsBrowse';
import MeetingShowSelected from './components/MeetingShowSelected';
import ErrorLogin from './components/ErrorLogin';
import ChatHome from './components/chat/ChatHome';
import { Route, BrowserRouter as Router, useLocation, useHistory, Redirect} from 'react-router-dom';
import {getMinutesRemaining, getCurrentTimestampString, getPageArray} from './utils/utils';
import {AppStoreProvider, AppStoreConsumer} from './stores/appStore';

class App extends React.Component {

  constructor(props){
    super(props);
    this.state={
      spinner:false,
      userId:null,
      password:null,
      loggedInUser:localStorage.getItem('loggedInUser') || '',
      loggedInUserFullName: localStorage.getItem('loggedInUserFullName')||'',
      admin:localStorage.getItem('admin')||"0",
      errorMessage:null,
      coursesArray:[],
      problemsArray:[],
      meetingsArray:[],
      currentProblemsPage:1,
      currentQuizesPage:1,
      currentCoursesPage:1,
      currentUsersPage:1,
      currentMeetingsPage:1,
      pageSize:30,
      quizesArray:[],
      usersArray:[],
      startedQuiz:localStorage.getItem('startedQuiz')?JSON.parse(localStorage.getItem('startedQuiz')):null,
      startedQuizAnswers:localStorage.getItem('startedQuizAnswers')?JSON.parse(localStorage.getItem('startedQuizAnswers')):null
    }

  }

  componentDidMount() {
    var timer = setInterval(() => {this.reviseQuizMinsRemaining();}, 60000)
  }








  startedQuizSubmit(){
    //console.log(this.state.startedQuizAnswers);
    //console.log(this.state.startedQuiz);

    var reqBody="quizId="+encodeURIComponent(this.state.startedQuiz.id);
        reqBody+="&quizType="+encodeURIComponent(this.state.startedQuiz.type);
        reqBody+='&quizInstanceId='+encodeURIComponent(this.state.startedQuiz.quizInstanceId);
        reqBody+='&answersObject='+encodeURIComponent(JSON.stringify(this.state.startedQuizAnswers));

        fetch(`/api/quizAnwersSubmit`, {
          headers:{
            'Accept':'application/json',
            'Content-type': 'application/x-www-form-urlencoded'
          },
          method: 'POST',
          body: reqBody
        })
        .then(res=>res.json())
        .then(
          data=>{
            //console.log(data);
            if(data.insertstatus=== "ok"){
              localStorage.removeItem('startedQuiz');
              localStorage.removeItem('startedQuizAnswers');
              this.setState({startedQuiz:null, startedQuizAnswers:null});
              alert('Quiz Submitted');
            }
          }
        );
  }

  reviseQuizMinsRemaining(){
    if(this.state.startedQuiz!==null){
          var mins_remaining= getMinutesRemaining(this.state.startedQuiz.start_time, this.state.startedQuiz.duration_minutes);

          if(mins_remaining>0){
            console.log(mins_remaining);
            const copied = Object.assign({}, this.state.startedQuiz);
            //copied['mins_remaining']=mins_remaining;
            this.setState({startedQuiz:copied});
            localStorage.setItem('startedQuiz', JSON.stringify(copied));
         }
         else{
           this.startedQuizSubmit();
         }
    }
  }



  isQuizStarted(quizId){
    if(this.state.startedQuiz!==null&&this.state.startedQuiz.id===quizId)
        return true;
    else
        return false;
  }


  loadProblems(){
    let url='/api/getProblems';

    if(this.state.currentProblemsPage!==undefined&&this.state.pageSize!==undefined){
      url+='?currentPage='+this.state.currentProblemsPage+'&pageSize='+this.state.pageSize;
    }

    this.setState({spinner:true});
    fetch(url, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
        this.setState({problemsArray: data, spinner: false});
    })
  }

  setProblems(arr){
    this.setState({problemsArray:arr});
  }

  setProblemsPage(page){
    this.setState({currentProblemsPage:page}, ()=>{this.loadProblems();});
  }

  nextProblemsPageClick(e){
    let pageArray=getPageArray(this.state.currentProblemsPage, this.state.problemsArray===undefined||this.state.problemsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentProblemsPage+1)){
        this.setState({currentProblemsPage:this.state.currentProblemsPage+1}, ()=>{this.loadProblems()});
    }
  }

  prevProblemsPageClick(e){
    let pageArray=getPageArray(this.state.currentProblemsPage, this.state.problemsArray===undefined||this.state.problemsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentProblemsPage-1)){
        this.setState({currentProblemsPage:this.state.currentProblemsPage-1}, ()=>{this.loadProblems()});
    }
  }

  loadMeetings=()=>{
    this.setState({spinner:true});
    let url='/api/getMeetings';

    if(this.state.currentMeetingsPage!==undefined&&this.state.pageSize!==undefined){
      url+='?currentPage='+this.state.currentMeetingsPage+'&pageSize='+this.state.pageSize;
    }

    fetch(url, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      console.log(data);
      this.setState({ meetingsArray: data, spinner: false});
    })
  }

  setMeetings=(arr)=>{
    this.setState({meetingsArray:arr});
  }

  setMeetingsPage=(page)=>{
    this.setState({currentMeetingsPage:page}, ()=>{this.loadMeetings();});
  }

  nextMeetingsPageClick=(e)=>{
    let pageArray=getPageArray(this.state.currentMeetingsPage, this.state.meetingsArray===undefined||this.state.meetingsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentMeetingsPage+1)){
        this.setState({currentMeetingsPage:this.state.currentMeetingsPage+1}, ()=>{this.loadMeetings()});
    }
  }

  prevMeetingsPageClick=(e)=>{
    let pageArray=getPageArray(this.state.currentMeetingsPage, this.state.meetingsArray===undefined||this.state.meetingsArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentMeetingsPage-1)){
        this.setState({currentMeetingsPage:this.state.currentMeetingsPage-1}, ()=>{this.loadMeetings()});
    }
  }


  loadQuizes(){
    this.setState({spinner:true});
    let url='/api/getQuizes';

    if(this.state.currentQuizesPage!==undefined&&this.state.pageSize!==undefined){
      url+='?currentPage='+this.state.currentQuizesPage+'&pageSize='+this.state.pageSize;
    }

    fetch(url, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      //console.log(data);
      this.setState({ quizesArray: data, spinner: false});
    })
  }

  setQuizes(arr){
    this.setState({quizesArray:arr});
  }

  setQuizesPage(page){
    this.setState({currentQuizesPage:page}, ()=>{this.loadQuizes();});
  }

  nextQuizesPageClick(e){
    let pageArray=getPageArray(this.state.currentQuizesPage, this.state.quizesArray===undefined||this.state.quizesArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentQuizesPage+1)){
        this.setState({currentQuizesPage:this.state.currentQuizesPage+1}, ()=>{this.loadQuizes()});
    }
  }

  prevQuizesPageClick(e){
    let pageArray=getPageArray(this.state.currentQuizesPage, this.state.quizesArray===undefined||this.state.quizesArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentQuizesPage-1)){
        this.setState({currentQuizesPage:this.state.currentQuizesPage-1}, ()=>{this.loadQuizes()});
    }
  }

  loadUsers(){
    this.setState({spinner:true});
    let url='/api/getUsers';

    if(this.state.currentUsersPage!==undefined&&this.state.pageSize!==undefined){
      url+='?currentPage='+this.state.currentUsersPage+'&pageSize='+this.state.pageSize;
    }

    fetch(url, {
      headers:{
        'Accept':'application/json'
      }
    })
    .then(res=>res.json())
    .then(data=>{
      this.setState({usersArray: data, spinner:false});
    })
  }

  setUsers(arr){
    this.setState({usersArray:arr});
  }

  setUsersPage(page){
    this.setState({currentUsersPage:page}, ()=>{this.loadUsers();});
  }

  nextUsersPageClick(e){
    let pageArray=getPageArray(this.state.currentUsersPage, this.state.usersArray===undefined||this.state.usersArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentUsersPage+1)){
        this.setState({currentUsersPage:this.state.currentUsersPage+1}, ()=>{this.loadUsers()});
    }
  }

  prevUsersPageClick(e){
    let pageArray=getPageArray(this.state.currentUsersPage, this.state.usersArray===undefined||this.state.usersArray.length<this.state.pageSize);
    if(pageArray.includes(this.state.currentUsersPage-1)){
        this.setState({currentUsersPage:this.state.currentUsersPage-1}, ()=>{this.loadUsers()});
    }
  }

  showSpinner(){
    this.setState({spinner:true});
  }

  hideSpinner(){
    this.setState({spinner:false});
  }

  handleAltLogin(strSource, response){
    const AltLoginInitiate=(firstName, lastName, email, id, picture_url)=>{
      var reqBody="userId="+encodeURIComponent(id);
      reqBody+='&firstName='+encodeURIComponent(firstName);
      reqBody+='&lastName='+encodeURIComponent(lastName);
      reqBody+='&email='+encodeURIComponent(email);
      reqBody+='&pictureUrl='+encodeURIComponent(picture_url);

      fetch(`/api/mergeUser`, {
        headers:{
          'Accept':'application/json',
          'Content-type': 'application/x-www-form-urlencoded'
        },
        method: 'POST',
        body: reqBody
      })
      .then(res=>res.json())
      .then(data=>{
          //console.log(data);
       });
       localStorage.setItem('loggedInUser', id);
       localStorage.setItem('loggedInUserFullName', firstName+' '+lastName);
       localStorage.setItem('admin', '0');
       this.setState({errorMessage:null,
                     loggedInUser: id,
                     loggedInUserFullName: firstName+' '+lastName,
                     admin: '0'
                     });
       //console.log(firstName+' '+lastName+' '+email+' '+id+' '+picture_url);
    };
    if(strSource==='facebook' && response.id!==undefined){
      let fullName=response.name;
      let firstName=fullName;
      let lastName="";
      let arrName=fullName.split(" ");
      if(arrName.length>1){
        firstName=arrName[0];
        lastName=arrName[1];
      }
      let email=response.email;
      let id=response.id;
      let picture_url=response.picture.data.url;
      AltLoginInitiate(firstName, lastName, email, id, picture_url);
    }
    else if(strSource==='google' && response.googleId!==undefined){
      let id=response.googleId;
      let picture_url=response.profileObj.imageUrl;
      let email=response.profileObj.email;
      let firstName=response.profileObj.givenName;
      let lastName=response.profileObj.familyName;
      AltLoginInitiate(firstName, lastName, email, id, picture_url);
      //console.log(response);
    }
    //alert(strSource);
  }

  handleLoginSubmit(event){
    if(this.state.userId===null
        ||this.state.password===null
        ||this.state.password.length===0
        ||this.state.userId.length===0)
    {
      event.preventDefault();
      this.setState({errorMessage: "UserId/password cannot be empty"})
      return;
    }
    var reqBody="userId="+encodeURIComponent(this.state.userId);
        reqBody+='&password='+encodeURIComponent(this.state.password);

        fetch(`/api/login`, {
          headers:{
            'Accept':'application/json',
            'Content-type': 'application/x-www-form-urlencoded'
          },
          method: 'POST',
          body: reqBody
        })
        .then(res=>res.json())
        .then(
          data=>{
            console.log(data);
            if(data.login==="ok"){
              localStorage.setItem('loggedInUser', this.state.userId);
              localStorage.setItem('loggedInUserFullName', data.full_name);
              localStorage.setItem('admin', data.admin);
              this.setState({errorMessage:null,
                            loggedInUser: this.state.userId,
                            loggedInUserFullName: data.full_name,
                            admin: data.admin
                            });
            }
            else{
              this.setState({errorMessage: "Login Failed: UserId/password Mismatch"});
            }
          }
        );
    event.preventDefault();

  }

  userIdChange(e){
    const newUserId=e.target.value;
    this.setState({userId: newUserId});
  }

  passwordChange(e){
    const newPassword=e.target.value;
    this.setState({password: newPassword});
  }

  logoutUser(e){
    //e.preventDefault();
    let confirmMsg='Are you sure you want to logout?';
    if(this.state.startedQuiz!==null){
        confirmMsg+='There is an unsubmitted quiz: \r\n'+this.state.startedQuiz.description;
    }
    let b=window.confirm(confirmMsg)
    if(b===true){
      if(this.state.startedQuiz!==null)
         this.startedQuizSubmit();
      localStorage.setItem('loggedInUser', '');
      localStorage.setItem('loggedInUserFullName','');
      localStorage.setItem('admin', "0");
      localStorage.removeItem('startedQuiz');
      localStorage.removeItem('startedQuizAnswers');
      this.setState({errorMessage:null, loggedInUser: '', admin:"0",
                      startedQuiz:null, startedQuizAnswers:null});
    }
    else{
      e.preventDefault();
    }
  }

  setErrorMessage(errMsg){
    this.setState({errorMessage:errMsg});
  }


    render(){
      return (
        <Router>
          <div>
            <AppStoreProvider>
              <AppStoreConsumer>
                {({state, dispatch})=>
                    (
                      <div>
                          <Route path="/" render={(props)=><Header
                                                              {...props}
                                                              state={state}
                                                              dispatch={dispatch}
                                                            />}
                          />
                          <Route path="/" render={(props)=><DropdownMenuHover
                                                            {...props}
                                                             state={state}
                                                             dispatch={dispatch}
                                                            />
                                                  }
                           />
                           {
                             state.spinner===true?(<div id="spinner" className="loadingContainer"><div className="loading"></div></div>):null
                           }
                          <Route exact path="/" component={ImageSlider} />
                          <Route exact path="/" component={Middle} />
                          <Route path="/problemsBrowse" render={(props)=><ProblemsBrowse {...props}
                                                                          state={state}
                                                                          dispatch={dispatch}
                                                                          />
                                                               }
                          />
                          <Route path="/problemsSearch" render={(props)=><ProblemsSearch {...props}
                                                                          state={state}
                                                                          dispatch={dispatch}
                                                                         />
                                                                }
                          />
                          <Route path="/problemInsert" render={(props)=><ProblemInsert {...props}
                                                                         state={state}
                                                                         dispatch={dispatch}
                                                                        />
                                                              }
                          />
                          <Route path="/problemShowSelected/:id" render={(props)=><ProblemShowSelected {...props}
                                                                                state={state}
                                                                                dispatch={dispatch}
                                                                               />
                                                                     }
                          />
                          <Route path="/quizesBrowse" render={(props)=><QuizesBrowse {...props}
                                                                        state={state}
                                                                        dispatch={dispatch}
                                                                      />
                                                             }
                           />
                           <Route path="/quizesSearch" render={(props)=><QuizesSearch {...props}
                                                                           state={state}
                                                                           dispatch={dispatch}
                                                                          />
                                                                 }
                           />
                          <Route path="/quizInsert" render={(props)=><QuizInsert {...props}
                                                                      state={state}
                                                                      dispatch={dispatch}
                                                                     />
                                                           }
                          />
                          <Route path="/quizShowSelected/:id" render={(props)=><QuizShowSelected {...props}
                                                                                state={state}
                                                                                dispatch={dispatch}
                                                                               />
                                                                     }
                          />
                          <Route path="/quizInstances/:id" render={(props)=><QuizInstances {...props}
                                                                                state={state}
                                                                                dispatch={dispatch}
                                                                            />
                                                                  }
                          />
                          <Route path="/usersBrowse" render={(props)=><UsersBrowse {...props}
                                                                        state={state}
                                                                        dispatch={dispatch}
                                                                      />
                                                            }
                          />
